// Procesar encuestas y calcular NPS por concesionario y tipo
const surveyData = $input.all();

if (!surveyData || surveyData.length === 0) {
  return [];
}

// Agrupar por concesionario y tipo de encuesta
const npsGroups = new Map();

surveyData.forEach(item => {
  const survey = item.json;

  const groupKey = `${survey.concesionario_id}_${survey.tipo_encuesta}`;

  if (!npsGroups.has(groupKey)) {
    npsGroups.set(groupKey, {
      concesionario_id: survey.concesionario_id,
      concesionario_nombre: survey.concesionario_nombre,
      tipo_encuesta: survey.tipo_encuesta,
      encuestas: [],

      // Contadores por clasificación
      promotores: 0,
      pasivos: 0,
      detractores: 0,

      // Métricas adicionales
      total_encuestas: 0,
      promedio_satisfaccion: 0,
      notas_bajas: 0,

      // Distribución por sucursal
      por_sucursal: new Map(),

      // Distribución por origen
      por_origen: new Map()
    });
  }

  const group = npsGroups.get(groupKey);

  // Agregar encuesta al grupo
  group.encuestas.push({
    id: survey.encuesta_id,
    score_nps: survey.score_nps,
    clasificacion: survey.clasificacion_nps,
    satisfaccion: survey.satisfaccion,
    es_nota_baja: survey.es_nota_baja,
    sucursal: survey.sucursal_nombre,
    origen: survey.origen,
    fecha: survey.fecha_completado,
    cliente: survey.nombre_cliente
  });

  // Incrementar contadores
  if (survey.clasificacion_nps === 'promotor') {
    group.promotores++;
  } else if (survey.clasificacion_nps === 'pasivo') {
    group.pasivos++;
  } else {
    group.detractores++;
  }

  if (survey.es_nota_baja) {
    group.notas_bajas++;
  }

  group.total_encuestas++;

  // Agrupar por sucursal
  const sucursalKey = survey.sucursal_nombre;
  if (!group.por_sucursal.has(sucursalKey)) {
    group.por_sucursal.set(sucursalKey, {
      nombre: sucursalKey,
      promotores: 0,
      pasivos: 0,
      detractores: 0,
      total: 0
    });
  }

  const sucursalData = group.por_sucursal.get(sucursalKey);
  sucursalData[survey.clasificacion_nps + 's']++;
  sucursalData.total++;

  // Agrupar por origen
  const origenKey = survey.origen;
  if (!group.por_origen.has(origenKey)) {
    group.por_origen.set(origenKey, {
      nombre: origenKey,
      promotores: 0,
      pasivos: 0,
      detractores: 0,
      total: 0
    });
  }

  const origenData = group.por_origen.get(origenKey);
  origenData[survey.clasificacion_nps + 's']++;
  origenData.total++;
});

// Calcular NPS y métricas para cada grupo
const npsResults = Array.from(npsGroups.values()).map(group => {
  // Calcular NPS: ((Promotores - Detractores) / Total) * 100
  const npsScore = group.total_encuestas > 0 
    ? Math.round(((group.promotores - group.detractores) / group.total_encuestas) * 100)
    : 0;

  // Calcular porcentajes
  const porcentajePromotores = group.total_encuestas > 0 
    ? Math.round((group.promotores / group.total_encuestas) * 100)
    : 0;

  const porcentajePasivos = group.total_encuestas > 0 
    ? Math.round((group.pasivos / group.total_encuestas) * 100)
    : 0;

  const porcentajeDetractores = group.total_encuestas > 0 
    ? Math.round((group.detractores / group.total_encuestas) * 100)
    : 0;

  // Promedio de satisfacción
  const promedioSatisfaccion = group.encuestas.length > 0
    ? Math.round((group.encuestas.reduce((sum, enc) => sum + (enc.satisfaccion || 0), 0) / group.encuestas.length) * 10) / 10
    : 0;

  // Clasificar NPS
  let clasificacionNPS = 'malo';
  if (npsScore >= 70) clasificacionNPS = 'excelente';
  else if (npsScore >= 50) clasificacionNPS = 'muy_bueno';
  else if (npsScore >= 30) clasificacionNPS = 'bueno';
  else if (npsScore >= 0) clasificacionNPS = 'regular';

  // Convertir Maps a arrays para JSON
  const sucursalesArray = Array.from(group.por_sucursal.values()).map(suc => ({
    ...suc,
    nps: suc.total > 0 ? Math.round(((suc.promotores - suc.detractores) / suc.total) * 100) : 0
  }));

  const origenesArray = Array.from(group.por_origen.values()).map(orig => ({
    ...orig,
    nps: orig.total > 0 ? Math.round(((orig.promotores - orig.detractores) / orig.total) * 100) : 0
  }));

  return {
    json: {
      ...group,
      por_sucursal: sucursalesArray,
      por_origen: origenesArray,

      // Métricas calculadas
      nps_score: npsScore,
      clasificacion_nps: clasificacionNPS,
      porcentaje_promotores: porcentajePromotores,
      porcentaje_pasivos: porcentajePasivos,
      porcentaje_detractores: porcentajeDetractores,
      promedio_satisfaccion: promedioSatisfaccion,
      porcentaje_notas_bajas: group.total_encuestas > 0 
        ? Math.round((group.notas_bajas / group.total_encuestas) * 100)
        : 0,

      // Metadata del cálculo
      calculo_timestamp: new Date().toISOString(),
      periodo_calculo: 'ultimas_4_horas',
      requiere_atencion: npsScore < 30 || group.porcentaje_notas_bajas > 20
    }
  };
});

console.log('NPS calculado por grupo:', {
  total_grupos: npsResults.length,
  post_venta: npsResults.filter(r => r.json.tipo_encuesta === 'post_venta').length,
  ventas: npsResults.filter(r => r.json.tipo_encuesta === 'ventas').length,
  requieren_atencion: npsResults.filter(r => r.json.requiere_atencion).length
});

return npsResults;

// Generar resumen completo de cálculos NPS
const processedData = $input.all();

const summary = {
  total_grupos_calculados: processedData.length,
  distribucion_por_tipo: processedData.reduce((acc, item) => {
    const tipo = item.json.tipo_encuesta;
    acc[tipo] = (acc[tipo] || 0) + 1;
    return acc;
  }, {}),

  // Estadísticas de NPS
  nps_promedio: processedData.length > 0 
    ? Math.round(processedData.reduce((sum, item) => sum + item.json.nps_score, 0) / processedData.length)
    : 0,

  distribucion_clasificacion: processedData.reduce((acc, item) => {
    const clasificacion = item.json.clasificacion_nps;
    acc[clasificacion] = (acc[clasificacion] || 0) + 1;
    return acc;
  }, {}),

  // Métricas de alerta
  grupos_requieren_atencion: processedData.filter(item => item.json.requiere_atencion).length,

  total_encuestas_procesadas: processedData.reduce((sum, item) => 
    sum + item.json.total_encuestas, 0
  ),

  // Distribución global de clasificaciones
  total_promotores: processedData.reduce((sum, item) => sum + item.json.promotores, 0),
  total_pasivos: processedData.reduce((sum, item) => sum + item.json.pasivos, 0),
  total_detractores: processedData.reduce((sum, item) => sum + item.json.detractores, 0),

  // Métricas de calidad
  promedio_satisfaccion_global: processedData.length > 0
    ? Math.round((processedData.reduce((sum, item) => 
        sum + item.json.promedio_satisfaccion, 0) / processedData.length) * 10) / 10
    : 0,

  total_notas_bajas: processedData.reduce((sum, item) => sum + item.json.notas_bajas, 0),

  // Metadata del procesamiento
  timestamp: new Date().toISOString(),
  execution_success: true,
  periodo_procesado: 'ultimas_4_horas'
};

// Calcular NPS global si hay datos
if (summary.total_encuestas_procesadas > 0) {
  summary.nps_global = Math.round(
    ((summary.total_promotores - summary.total_detractores) / 
     summary.total_encuestas_procesadas) * 100
  );

  summary.porcentaje_notas_bajas_global = Math.round(
    (summary.total_notas_bajas / summary.total_encuestas_procesadas) * 100
  );
}

console.log('Cálculos NPS completados:', summary);

return [{ json: summary }];

// No hay encuestas que procesar
console.log('No hay encuestas completadas que requieran cálculo de NPS');

return [{
  json: {
    message: 'No hay encuestas completadas para calcular NPS',
    timestamp: new Date().toISOString(),
    execution_success: true,
    surveys_found: 0
  }
}];
