// SALES SURVEY CALL CENTER ASSIGNMENT - SRP: Solo asignaci贸n equitativa de encuestas VENTAS
// Principio SRP: Una sola responsabilidad - distribuci贸n equitativa de encuestas de ventas

const assignmentData = $('Trigger Asignaci贸n Contact Center Ventas').first().json;
const contactCenterAgents = $('Obtener Agentes Contact Center Ventas').first().json;
const salesClientsToAssign = assignmentData.sales_clients_for_call_center;

// Validar entrada espec铆fica para VENTAS
if (!assignmentData.tenant_id) {
  throw new Error('tenant_id es requerido para asignaci贸n de encuestas de VENTAS');
}

if (!salesClientsToAssign || salesClientsToAssign.length === 0) {
  throw new Error('No hay clientes de VENTAS para asignar a Contact Center');
}

// Validar que hay agentes disponibles
if (!contactCenterAgents || contactCenterAgents.length === 0) {
  throw new Error('No hay agentes de Contact Center activos para este concesionario (VENTAS)');
}

console.log('Iniciando asignaci贸n de encuestas VENTAS:', {
  tenantId: assignmentData.tenant_id,
  clientesVentas: salesClientsToAssign.length,
  agentesDisponibles: contactCenterAgents.length
});

// Distribuci贸n equitativa espec铆fica para VENTAS (algoritmo round-robin)
const salesAssignments = [];
let agentIndex = 0;

salesClientsToAssign.forEach((salesClient, index) => {
  const assignedAgent = contactCenterAgents[agentIndex];

  salesAssignments.push({
    // Datos del cliente de VENTAS
    cliente_nombre: salesClient.cliente_nombre,
    cliente_telefono: salesClient.cliente_telefono,
    vehiculo_modelo: salesClient.vehiculo_modelo,
    fecha_venta: salesClient.fecha_venta,
    asesor_ventas_id: salesClient.asesor_ventas_id,
    sucursal: salesClient.sucursal,
    tiempo_sin_respuesta: salesClient.tiempo_sin_respuesta,
    fecha_envio_whatsapp: salesClient.fecha_envio_whatsapp,

    // Datos del agente asignado
    agente_id: assignedAgent.id,
    agente_nombre: assignedAgent.nombre,
    agente_email: assignedAgent.email,
    agente_telefono: assignedAgent.telefono,

    // Metadatos de asignaci贸n espec铆ficos VENTAS
    fecha_asignacion: new Date().toISOString(),
    prioridad: salesClient.tiempo_sin_respuesta > 12 ? 'alta' : 'normal', // >12 horas sin respuesta
    tenant_id: assignmentData.tenant_id,
    survey_type: 'VENTAS',
    department: 'VENTAS'
  });

  // Rotar al siguiente agente
  agentIndex = (agentIndex + 1) % contactCenterAgents.length;
});

// Agrupar por agente para notificaciones eficientes espec铆ficas de VENTAS
const salesAssignmentsByAgent = salesAssignments.reduce((acc, assignment) => {
  const agentId = assignment.agente_id;
  if (!acc[agentId]) {
    acc[agentId] = {
      agent_info: {
        id: assignment.agente_id,
        nombre: assignment.agente_nombre,
        email: assignment.agente_email,
        telefono: assignment.agente_telefono
      },
      assigned_sales_clients: []
    };
  }
  acc[agentId].assigned_sales_clients.push(assignment);
  return acc;
}, {});

console.log('Asignaci贸n VENTAS completada:', {
  totalAsignaciones: salesAssignments.length,
  totalAgentes: contactCenterAgents.length,
  clientesPorAgente: Math.round(salesAssignments.length / contactCenterAgents.length * 100) / 100
});

return {
  tenant_id: assignmentData.tenant_id,
  survey_type: 'VENTAS',
  department: 'VENTAS',
  total_sales_assignments: salesAssignments.length,
  total_agents: contactCenterAgents.length,
  sales_assignments_by_agent: salesAssignmentsByAgent,
  assignment_stats: {
    sales_clients_per_agent: Math.round(salesAssignments.length / contactCenterAgents.length * 100) / 100,
    high_priority_count: salesAssignments.filter(a => a.prioridad === 'alta').length,
    assignment_timestamp: new Date().toISOString()
  },
  campaign_metadata: assignmentData.campaign_metadata
};

// Generar reporte final de asignaciones VENTAS
// Principio SRP: Una sola responsabilidad - generar reporte final

const assignmentData = $('Asignar Clientes Ventas').first().json;
const notificationResults = $input.all();

const finalSalesReport = {
  campaign_id: assignmentData.campaign_metadata?.campaign_id || `sales_campaign_${Date.now()}`,
  tenant_id: assignmentData.tenant_id,
  survey_type: 'VENTAS',
  department: 'VENTAS',
  assignment_summary: {
    total_sales_clients_assigned: assignmentData.total_sales_assignments,
    total_agents: assignmentData.total_agents,
    sales_assignments_per_agent: assignmentData.assignment_stats.sales_clients_per_agent,
    high_priority_sales_clients: assignmentData.assignment_stats.high_priority_count
  },
  notification_results: {
    emails_sent: notificationResults.filter(r => r.json?.message_id).length,
    emails_failed: notificationResults.length - notificationResults.filter(r => r.json?.message_id).length
  },
  completion_timestamp: new Date().toISOString(),
  status: 'completed'
};

console.log('Reporte final de asignaci贸n VENTAS:', {
  tenantId: finalSalesReport.tenant_id,
  tipo: 'VENTAS',
  clientesAsignados: finalSalesReport.assignment_summary.total_sales_clients_assigned,
  emailsEnviados: finalSalesReport.notification_results.emails_sent
});

return finalSalesReport;

