// SALES SURVEY WHATSAPP SENDER - SRP: Solo env√≠o individual WhatsApp VENTAS
// Principio SRP: Una sola responsabilidad - preparar mensaje WhatsApp individual para ventas

const salesClient = $input.first().json;
const tenantConfig = $('Trigger Enviador WhatsApp Ventas').first().json.tenant_config;

// Validar datos espec√≠ficos de VENTAS
if (!salesClient.cliente_nombre || !salesClient.cliente_telefono) {
  throw new Error('Datos de cliente de VENTAS incompletos para env√≠o WhatsApp');
}

if (!salesClient.vehiculo_modelo || !salesClient.fecha_venta) {
  throw new Error('Datos de venta requeridos: vehiculo_modelo y fecha_venta');
}

if (!tenantConfig || !tenantConfig.whatsapp_token) {
  throw new Error('Configuraci√≥n WhatsApp no encontrada para este concesionario (VENTAS)');
}

// Preparar mensaje personalizado espec√≠fico para VENTAS
const phoneNumber = salesClient.cliente_telefono.replace(/[^0-9]/g, ''); // Limpiar formato
const fechaVentaFormatted = new Date(salesClient.fecha_venta).toLocaleDateString('es-CL');

const salesSurveyMessage = {
  // Configuraci√≥n WhatsApp espec√≠fica del tenant
  phone_number_id: tenantConfig.phone_number_id,
  access_token: tenantConfig.whatsapp_token,

  // Datos del destinatario
  to_phone: phoneNumber,

  // Datos espec√≠ficos de la VENTA para personalizar mensaje
  customer_data: {
    nombre: salesClient.cliente_nombre,
    vehiculo: salesClient.vehiculo_modelo,
    fecha_compra: fechaVentaFormatted,
    asesor_ventas: salesClient.asesor_ventas_nombre || 'nuestro equipo'
  },

  // Mensaje personalizado espec√≠fico para VENTAS (diferente al post-servicio)
  message_text: `¬°Hola ${salesClient.cliente_nombre}! üëã\n\n` +
    `Esperamos que est√© disfrutando su nuevo ${salesClient.vehiculo_modelo} adquirido el ${fechaVentaFormatted} üöó\n\n` +
    `En ${tenantConfig.business_name} nos importa mucho su experiencia de compra. ¬øPodr√≠a ayudarnos respondiendo una breve encuesta sobre su proceso de compra?\n\n` +
    `üìù Solo tomar√° 2 minutos y nos ayudar√° a mejorar nuestro servicio de ventas.\n\n` +
    `üëÜ Haga clic aqu√≠ para responder: ${tenantConfig.survey_base_url}/ventas/qr?id=${salesClient.survey_token}\n\n` +
    `¬°Gracias por elegirnos para su nueva adquisici√≥n! ‚ú®`,

  // Metadatos espec√≠ficos de VENTAS
  survey_metadata: {
    survey_id: salesClient.survey_id,
    survey_type: 'VENTAS',
    department: 'VENTAS',
    tenant_id: salesClient.concesionario_id,
    client_phone: phoneNumber,
    vehicle_model: salesClient.vehiculo_modelo,
    sale_date: salesClient.fecha_venta,
    sales_advisor_id: salesClient.asesor_ventas_id
  }
};

console.log('Preparando env√≠o WhatsApp individual VENTAS:', {
  cliente: salesClient.cliente_nombre,
  telefono: phoneNumber,
  vehiculo: salesClient.vehiculo_modelo,
  fechaVenta: fechaVentaFormatted,
  tenantId: salesClient.concesionario_id,
  tipo: 'VENTAS'
});

return salesSurveyMessage;

// Generar resultado de env√≠o individual VENTAS
// Principio SRP: Una sola responsabilidad - reportar resultado de env√≠o

const salesMessageData = $('Preparar Mensaje WhatsApp Ventas').first().json;
const whatsappResponse = $('Enviar WhatsApp Ventas').first().json;
const updateResult = $('Actualizar Estado Encuesta Ventas').first().json;

// Determinar estado del env√≠o
const deliveryStatus = whatsappResponse.messages?.[0]?.id ? 'sent' : 'failed';
const messageId = whatsappResponse.messages?.[0]?.id || null;
const errorInfo = whatsappResponse.error || null;

const salesDeliveryResult = {
  status: deliveryStatus === 'sent' ? 'success' : 'error',
  message: deliveryStatus === 'sent' 
    ? 'Encuesta de VENTAS enviada exitosamente por WhatsApp' 
    : 'Error al enviar encuesta de VENTAS por WhatsApp',
  survey_type: 'VENTAS',
  department: 'VENTAS',

  client_data: {
    nombre: salesMessageData.customer_data.nombre,
    telefono: salesMessageData.to_phone,
    vehiculo: salesMessageData.customer_data.vehiculo,
    fecha_compra: salesMessageData.customer_data.fecha_compra
  },

  delivery_info: {
    whatsapp_message_id: messageId,
    delivery_status: deliveryStatus,
    phone_number_id: salesMessageData.phone_number_id,
    sent_at: new Date().toISOString(),
    error_details: errorInfo
  },

  survey_info: {
    survey_id: salesMessageData.survey_metadata.survey_id,
    tenant_id: salesMessageData.survey_metadata.tenant_id,
    survey_type: 'VENTAS',
    expected_response_window: '6 horas',
    fallback_action: 'Asignar a Contact Center si no responde'
  }
};

console.log('Resultado env√≠o WhatsApp individual VENTAS:', {
  cliente: salesDeliveryResult.client_data.nombre,
  vehiculo: salesDeliveryResult.client_data.vehiculo,
  status: salesDeliveryResult.status,
  messageId: salesDeliveryResult.delivery_info.whatsapp_message_id,
  tipo: 'VENTAS'
});

return salesDeliveryResult;

