// SALES SURVEY DUPLICATE FILTER - SRP: Solo filtrar duplicados en VENTAS
// Principio SRP: Una sola responsabilidad - validar entrada y preparar consulta

const filterRequest = $input.first().json;

// Validar estructura de entrada específica para VENTAS
if (!filterRequest.tenant_id) {
  throw new Error('tenant_id es requerido para filtro de duplicados VENTAS');
}

if (!filterRequest.sales_clients_excel || !Array.isArray(filterRequest.sales_clients_excel)) {
  throw new Error('sales_clients_excel debe ser un array de clientes de ventas');
}

if (filterRequest.sales_clients_excel.length === 0) {
  throw new Error('No hay clientes de VENTAS para procesar en el filtro');
}

// Validar que cada cliente tenga los campos mínimos requeridos para VENTAS
const invalidClients = filterRequest.sales_clients_excel.filter(client => 
  !client.cliente_telefono || 
  !client.vehiculo_modelo || 
  !client.fecha_venta
);

if (invalidClients.length > 0) {
  throw new Error(`${invalidClients.length} clientes de VENTAS tienen datos incompletos (teléfono, vehículo o fecha de venta faltante)`);
}

// Extraer teléfonos únicos para consulta eficiente
const uniquePhones = [...new Set(
  filterRequest.sales_clients_excel.map(client => 
    client.cliente_telefono.replace(/[^0-9+]/g, '') // Normalizar formato
  )
)];

console.log('Iniciando filtro de duplicados VENTAS:', {
  tenantId: filterRequest.tenant_id,
  totalClientesExcel: filterRequest.sales_clients_excel.length,
  telefonosUnicos: uniquePhones.length,
  tipo: 'VENTAS'
});

return {
  tenant_id: filterRequest.tenant_id,
  survey_type: 'VENTAS',
  department: 'VENTAS',
  original_sales_clients: filterRequest.sales_clients_excel,
  unique_phones: uniquePhones,
  total_clients_to_check: filterRequest.sales_clients_excel.length,
  filter_metadata: {
    excel_upload_timestamp: filterRequest.upload_timestamp,
    uploaded_by: filterRequest.uploaded_by,
    campaign_id: filterRequest.campaign_id || `sales_campaign_${Date.now()}`
  }
};

// SALES SURVEY DUPLICATE DETECTION - SRP: Solo detectar duplicados específicos VENTAS
// Principio SRP: Una sola responsabilidad - identificar duplicados basado en teléfono + vehículo + fecha

const filterData = $('Validar Entrada Filtro Ventas').first().json;
const existingSalesSurveys = $('Consultar Encuestas Ventas Existentes').first().json;

// Crear mapa de encuestas existentes con clave compuesta específica para VENTAS
// Clave: teléfono + vehículo + fecha_venta (más específico que solo teléfono)
const existingSalesMap = new Map();

existingSalesSurveys.forEach(survey => {
  const normalizedPhone = survey.cliente_telefono.replace(/[^0-9+]/g, '');
  const surveyDate = new Date(survey.fecha_venta).toDateString();
  const compositeKey = `${normalizedPhone}_${survey.vehiculo_modelo}_${surveyDate}`;

  existingSalesMap.set(compositeKey, {
    telefono: survey.cliente_telefono,
    vehiculo: survey.vehiculo_modelo,
    fecha_venta: survey.fecha_venta,
    origen: survey.origen,
    fecha_respuesta: survey.created_at
  });
});

// Filtrar clientes de VENTAS, eliminando duplicados
const filteredSalesClients = [];
const duplicatesFound = [];

filterData.original_sales_clients.forEach(client => {
  const normalizedPhone = client.cliente_telefono.replace(/[^0-9+]/g, '');
  const clientDate = new Date(client.fecha_venta).toDateString();
  const compositeKey = `${normalizedPhone}_${client.vehiculo_modelo}_${clientDate}`;

  if (existingSalesMap.has(compositeKey)) {
    // Duplicado encontrado - cliente ya respondió encuesta de VENTAS
    const existingSurvey = existingSalesMap.get(compositeKey);
    duplicatesFound.push({
      cliente_excel: client,
      encuesta_existente: existingSurvey,
      razon_duplicado: 'Ya respondió encuesta de VENTAS para este vehículo y fecha',
      tipo_duplicado: 'VENTAS_EXACTO'
    });
  } else {
    // Cliente único - puede recibir encuesta de VENTAS
    filteredSalesClients.push({
      ...client,
      normalized_phone: normalizedPhone,
      composite_key: compositeKey
    });
  }
});

// Generar estadísticas específicas de VENTAS
const filterStats = {
  input_sales_clients: filterData.original_sales_clients.length,
  existing_sales_surveys: existingSalesSurveys.length,
  duplicates_found: duplicatesFound.length,
  filtered_sales_clients: filteredSalesClients.length,
  filter_efficiency: Math.round((duplicatesFound.length / filterData.original_sales_clients.length) * 100),
  processing_timestamp: new Date().toISOString()
};

console.log('Filtro de duplicados VENTAS completado:', {
  tenantId: filterData.tenant_id,
  tipo: 'VENTAS',
  clientesOriginales: filterStats.input_sales_clients,
  duplicadosEncontrados: filterStats.duplicates_found,
  clientesFiltrados: filterStats.filtered_sales_clients,
  eficienciaFiltro: `${filterStats.filter_efficiency}%`
});

return {
  tenant_id: filterData.tenant_id,
  survey_type: 'VENTAS',
  department: 'VENTAS',
  filter_results: {
    filtered_sales_clients: filteredSalesClients,
    duplicates_found: duplicatesFound,
    filter_stats: filterStats
  },
  campaign_metadata: filterData.filter_metadata
};

// Preparar reporte detallado de filtrado VENTAS con duplicados
// Principio SRP: Una sola responsabilidad - generar reporte completo

const filterResults = $input.first().json;

// Reporte completo específico para VENTAS
const salesFilterReport = {
  status: 'success',
  message: 'Filtro de duplicados VENTAS completado exitosamente',
  survey_type: 'VENTAS',
  department: 'VENTAS',
  tenant_id: filterResults.tenant_id,

  summary: {
    clientes_excel_originales: filterResults.filter_results.filter_stats.input_sales_clients,
    encuestas_existentes_encontradas: filterResults.filter_results.filter_stats.existing_sales_surveys,
    duplicados_eliminados: filterResults.filter_results.filter_stats.duplicates_found,
    clientes_listos_whatsapp: filterResults.filter_results.filter_stats.filtered_sales_clients,
    eficiencia_filtro_porcentaje: filterResults.filter_results.filter_stats.filter_efficiency
  },

  ready_for_whatsapp: {
    clients_count: filterResults.filter_results.filtered_sales_clients.length,
    estimated_sending_time: `${Math.ceil(filterResults.filter_results.filtered_sales_clients.length / 10)} minutos`,
    next_step: 'Envío masivo por WhatsApp'
  },

  duplicates_details: filterResults.filter_results.duplicates_found.map(dup => ({
    cliente_nombre: dup.cliente_excel.cliente_nombre,
    telefono: dup.cliente_excel.cliente_telefono,
    vehiculo: dup.cliente_excel.vehiculo_modelo,
    fecha_venta: dup.cliente_excel.fecha_venta,
    razon: dup.razon_duplicado,
    fecha_respuesta_previa: dup.encuesta_existente.fecha_respuesta,
    origen_respuesta_previa: dup.encuesta_existente.origen
  })),

  campaign_info: filterResults.campaign_metadata,
  processed_at: new Date().toISOString()
};

console.log('Reporte de filtro VENTAS generado:', {
  tenantId: salesFilterReport.tenant_id,
  tipo: 'VENTAS',
  clientesListos: salesFilterReport.ready_for_whatsapp.clients_count,
  duplicadosEliminados: salesFilterReport.summary.duplicados_eliminados
});

return salesFilterReport;

// Preparar respuesta para caso sin clientes válidos VENTAS
// Principio SRP: Una sola responsabilidad - manejar caso sin clientes

const filterResults = $input.first().json;

const noClientsSalesReport = {
  status: 'warning',
  message: 'Todos los clientes de VENTAS ya respondieron la encuesta - no hay envíos necesarios',
  survey_type: 'VENTAS',
  department: 'VENTAS',
  tenant_id: filterResults.tenant_id,

  summary: {
    clientes_excel_originales: filterResults.filter_results.filter_stats.input_sales_clients,
    todos_ya_respondieron: true,
    duplicados_eliminados: filterResults.filter_results.filter_stats.duplicates_found,
    clientes_listos_whatsapp: 0
  },

  duplicates_details: filterResults.filter_results.duplicates_found.map(dup => ({
    cliente_nombre: dup.cliente_excel.cliente_nombre,
    telefono: dup.cliente_excel.cliente_telefono,
    vehiculo: dup.cliente_excel.vehiculo_modelo,
    razon: dup.razon_duplicado,
    origen_respuesta_previa: dup.encuesta_existente.origen
  })),

  recommendation: 'Revisar si hay nuevos clientes de ventas para encuestar o ajustar criterios de filtrado',
  processed_at: new Date().toISOString()
};

console.log('Reporte VENTAS sin clientes válidos:', {
  tenantId: noClientsSalesReport.tenant_id,
  tipo: 'VENTAS',
  todosYaRespondieron: true,
  duplicados: noClientsSalesReport.summary.duplicados_eliminados
});

return noClientsSalesReport;
