// Procesar leads y preparar actualizaciones de estado
const leadsData = $input.all();

if (!leadsData || leadsData.length === 0) {
  return [];
}

// Procesar cada lead seg√∫n la acci√≥n sugerida
const processedUpdates = leadsData.map(item => {
  const lead = item.json;

  let newStatus = lead.estado;
  let updateReason = '';
  let requiresNotification = false;
  let updatePriority = 'normal';
  let additionalData = {};

  switch (lead.accion_sugerida) {
    case 'marcar_contactado':
      newStatus = 'contactado';
      updateReason = 'Contacto detectado autom√°ticamente por actividad registrada';
      requiresNotification = false;
      additionalData.fecha_primer_contacto = new Date().toISOString();
      break;

    case 'marcar_cotizado':
      newStatus = 'cotizado';
      updateReason = 'Cotizaci√≥n detectada autom√°ticamente por monto registrado';
      requiresNotification = true;
      updatePriority = 'high';
      additionalData.fecha_cotizacion = new Date().toISOString();
      break;

    case 'marcar_vendido':
      newStatus = 'vendido';
      updateReason = 'Venta detectada autom√°ticamente por notas de interacci√≥n';
      requiresNotification = true;
      updatePriority = 'high';
      additionalData.fecha_cierre = new Date().toISOString();
      break;

    case 'marcar_perdido_tiempo':
      newStatus = 'perdido';
      updateReason = 'Lead marcado como perdido autom√°ticamente por inactividad (>45 d√≠as)';
      requiresNotification = true;
      updatePriority = 'medium';
      additionalData.fecha_cierre = new Date().toISOString();
      additionalData.motivo_perdida = 'Inactividad prolongada - m√°s de 45 d√≠as sin contacto';
      break;

    default:
      return null; // Skip si no hay acci√≥n v√°lida
  }

  // Solo procesar si realmente hay cambio de estado
  if (newStatus === lead.estado) {
    return null;
  }

  return {
    json: {
      // Datos del lead
      lead_id: lead.id,
      concesionario_id: lead.concesionario_id,
      asesor_id: lead.asesor_asignado_id,

      // Informaci√≥n del lead
      cliente_nombre: lead.nombre_cliente,
      telefono_cliente: lead.telefono_cliente,
      modelo_interes: lead.modelo_interes,
      score_calidad: lead.score_calidad,

      // Cambio de estado
      estado_anterior: lead.estado,
      estado_nuevo: newStatus,
      accion_ejecutada: lead.accion_sugerida,

      // Metadatos de actualizaci√≥n
      update_reason: updateReason,
      requires_notification: requiresNotification,
      update_priority: updatePriority,
      update_timestamp: new Date().toISOString(),

      // Datos adicionales a actualizar
      additional_data: additionalData,

      // Informaci√≥n para notificaciones
      asesor_nombre: lead.asesor_nombre,
      asesor_email: lead.asesor_email,
      concesionario_nombre: lead.concesionario_nombre,

      // M√©tricas de tiempo
      horas_desde_creacion: Math.round(lead.horas_desde_creacion * 10) / 10,
      actividad_reciente: lead.actividad_reciente
    }
  };
}).filter(item => item !== null);

console.log('Actualizaciones de estado procesadas:', {
  total_leads_evaluados: leadsData.length,
  actualizaciones_a_realizar: processedUpdates.length,
  por_accion: processedUpdates.reduce((acc, item) => {
    const accion = item.json.accion_ejecutada;
    acc[accion] = (acc[accion] || 0) + 1;
    return acc;
  }, {})
});

return processedUpdates;

// Generar m√©tricas de las actualizaciones procesadas
const processedData = $input.all();

const summary = {
  total_actualizaciones: processedData.length,

  // Distribuci√≥n por tipo de actualizaci√≥n
  por_accion: processedData.reduce((acc, item) => {
    const accion = item.json.accion_ejecutada;
    acc[accion] = (acc[accion] || 0) + 1;
    return acc;
  }, {}),

  // Distribuci√≥n por estado final
  por_estado_final: processedData.reduce((acc, item) => {
    const estado = item.json.estado_nuevo;
    acc[estado] = (acc[estado] || 0) + 1;
    return acc;
  }, {}),

  // M√©tricas de prioridad
  por_prioridad: processedData.reduce((acc, item) => {
    const prioridad = item.json.update_priority;
    acc[prioridad] = (acc[prioridad] || 0) + 1;
    return acc;
  }, {}),

  // Notificaciones enviadas
  notificaciones_enviadas: processedData.filter(item => 
    item.json.requires_notification
  ).length,

  // M√©tricas de conversi√≥n
  conversiones_detectadas: {
    a_contactado: processedData.filter(item => item.json.estado_nuevo === 'contactado').length,
    a_cotizado: processedData.filter(item => item.json.estado_nuevo === 'cotizado').length,
    a_vendido: processedData.filter(item => item.json.estado_nuevo === 'vendido').length,
    a_perdido: processedData.filter(item => item.json.estado_nuevo === 'perdido').length
  },

  // Leads con actividad reciente
  con_actividad_reciente: processedData.filter(item => 
    item.json.actividad_reciente
  ).length,

  // Tiempo promedio desde creaci√≥n
  tiempo_promedio_horas: processedData.length > 0 
    ? Math.round((processedData.reduce((sum, item) => 
        sum + item.json.horas_desde_creacion, 0) / processedData.length) * 10) / 10
    : 0,

  // Metadata del procesamiento
  timestamp: new Date().toISOString(),
  execution_success: true
};

console.log('Actualizaciones de estados completadas:', summary);

return [{ json: summary }];

// Procesar webhook manual para actualizaci√≥n espec√≠fica
const inputData = $input.first().json;

// Validar entrada manual
if (!inputData.lead_id || !inputData.new_status || !inputData.user_id) {
  throw new Error('Faltan campos requeridos: lead_id, new_status, user_id');
}

// Estados v√°lidos
const validStates = ['nuevo', 'contactado', 'cotizado', 'vendido', 'perdido'];
if (!validStates.includes(inputData.new_status)) {
  throw new Error(`Estado inv√°lido: ${inputData.new_status}. Estados v√°lidos: ${validStates.join(', ')}`);
}

// Preparar actualizaci√≥n manual
const manualUpdate = {
  lead_id: inputData.lead_id,
  concesionario_id: inputData.tenant_id,
  new_status: inputData.new_status,
  update_reason: inputData.reason || 'Actualizaci√≥n manual',
  update_notes: inputData.notes || '',
  user_id: inputData.user_id,
  user_name: inputData.user_name || 'Usuario Manual',

  // Datos adicionales seg√∫n el nuevo estado
  additional_data: {},

  // Metadata
  is_manual_update: true,
  update_timestamp: new Date().toISOString(),
  requires_notification: inputData.notify_team || false
};

// Configurar datos adicionales seg√∫n el estado
switch (inputData.new_status) {
  case 'contactado':
    manualUpdate.additional_data.fecha_primer_contacto = new Date().toISOString();
    break;
  case 'cotizado':
    manualUpdate.additional_data.fecha_cotizacion = new Date().toISOString();
    if (inputData.quote_amount) {
      manualUpdate.additional_data.monto_cotizacion = inputData.quote_amount;
    }
    break;
  case 'vendido':
    manualUpdate.additional_data.fecha_cierre = new Date().toISOString();
    if (inputData.sale_amount) {
      manualUpdate.additional_data.monto_venta = inputData.sale_amount;
    }
    break;
  case 'perdido':
    manualUpdate.additional_data.fecha_cierre = new Date().toISOString();
    manualUpdate.additional_data.motivo_perdida = inputData.loss_reason || 'Motivo no especificado';
    break;
}

console.log('Actualizaci√≥n manual procesada:', {
  lead_id: manualUpdate.lead_id,
  new_status: manualUpdate.new_status,
  user_id: manualUpdate.user_id,
  is_manual: manualUpdate.is_manual_update
});

return manualUpdate;

// No hay leads que requieran actualizaci√≥n
console.log('No hay leads que requieran actualizaci√≥n de estado en este momento');

return [{
  json: {
    message: 'No hay leads que requieran actualizaci√≥n de estado',
    timestamp: new Date().toISOString(),
    execution_success: true,
    leads_evaluated: 0,
    updates_performed: 0
  }
}];
