// Procesar y clasificar cotizaciones para seguimiento
const quotationData = $input.all();

if (!quotationData || quotationData.length === 0) {
  return [];
}

// Agrupar por tipo de seguimiento y prioridad
const processedQuotations = quotationData.map(item => {
  const quotation = item.json;

  // Skip si no necesita seguimiento
  if (quotation.tipo_seguimiento === 'no_seguimiento') {
    return null;
  }

  // Determinar estrategia de contacto
  let contactStrategy = [];
  let messageTemplate = '';
  let urgencyLevel = 'normal';

  switch (quotation.tipo_seguimiento) {
    case 'seguimiento_4h':
      contactStrategy = ['whatsapp', 'email'];
      messageTemplate = 'initial_followup';
      urgencyLevel = 'alta';
      break;

    case 'seguimiento_1_3d':
      contactStrategy = ['whatsapp', 'call'];
      messageTemplate = 'early_followup';
      urgencyLevel = 'alta';
      break;

    case 'seguimiento_4_7d':
      contactStrategy = ['call', 'whatsapp'];
      messageTemplate = 'persistent_followup';
      urgencyLevel = 'media';
      break;

    case 'seguimiento_8_15d':
      contactStrategy = ['email', 'whatsapp'];
      messageTemplate = 'retention_attempt';
      urgencyLevel = 'media';
      break;

    case 'seguimiento_16_30d':
      contactStrategy = ['call'];
      messageTemplate = 'final_attempt';
      urgencyLevel = 'baja';
      break;

    case 'perdido_definitivo':
      contactStrategy = [];
      messageTemplate = 'mark_as_lost';
      urgencyLevel = 'archivado';
      break;

    default:
      return null;
  }

  // Personalizar mensaje seg√∫n contexto
  const contextualData = {
    cliente_nombre: quotation.nombre_cliente,
    modelo_interes: quotation.modelo_interes,
    monto_cotizacion: new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP'
    }).format(quotation.monto_cotizacion),
    dias_transcurridos: Math.floor(quotation.dias_desde_cotizacion),
    asesor_nombre: quotation.asesor_nombre,
    concesionario_nombre: quotation.concesionario_nombre
  };

  return {
    json: {
      ...quotation,
      contact_strategy: contactStrategy,
      message_template: messageTemplate,
      urgency_level: urgencyLevel,
      contextual_data: contextualData,
      follow_up_id: `${quotation.concesionario_id}_${quotation.id}_${Date.now()}`,
      should_notify_supervisor: quotation.prioridad_seguimiento === 'muy_alta' && quotation.seguimientos_realizados >= 2
    }
  };
}).filter(item => item !== null);

console.log('Cotizaciones procesadas para seguimiento:', {
  total_cotizaciones: processedQuotations.length,
  por_tipo: processedQuotations.reduce((acc, item) => {
    const tipo = item.json.tipo_seguimiento;
    acc[tipo] = (acc[tipo] || 0) + 1;
    return acc;
  }, {}),
  alta_prioridad: processedQuotations.filter(item => item.json.prioridad_seguimiento === 'muy_alta').length
});

return processedQuotations;

