};

// Seleccionar mensajes seg√∫n template
const selectedMessages = messageTemplates[message_template] || {};

// Preparar output con mensajes personalizados
const messageOutput = {
  ...quotationData,
  personalized_messages: selectedMessages,
  generated_at: new Date().toISOString()
};

console.log('Mensajes generados para seguimiento:', {
  lead_id: quotationData.id,
  template: message_template,
  strategies: quotationData.contact_strategy,
  urgency: quotationData.urgency_level
});

return messageOutput;

// Generar m√©tricas de seguimientos procesados
const processedData = $input.all();

const summary = {
  total_seguimientos_realizados: processedData.length,
  por_tipo: processedData.reduce((acc, item) => {
    const tipo = item.json.tipo_seguimiento;
    acc[tipo] = (acc[tipo] || 0) + 1;
    return acc;
  }, {}),
  por_prioridad: processedData.reduce((acc, item) => {
    const prioridad = item.json.prioridad_seguimiento;
    acc[prioridad] = (acc[prioridad] || 0) + 1;
    return acc;
  }, {}),
  supervisores_notificados: processedData.filter(item => 
    item.json.should_notify_supervisor
  ).length,
  cotizaciones_perdidas: processedData.filter(item => 
    item.json.urgency_level === 'archivado'
  ).length,
  valor_total_seguimiento: processedData.reduce((sum, item) => 
    sum + (item.json.monto_cotizacion || 0), 0
  ),
  timestamp: new Date().toISOString(),
  execution_success: true
};

console.log('Seguimientos procesados:', summary);

return [{ json: summary }];

// No hay cotizaciones que requieran seguimiento
