// Validar y procesar cambios en reclamos para auditoría
const inputData = $input.first().json;

// Validar estructura básica
const requiredFields = ['complaint_id', 'tenant_id', 'user_id', 'changes'];
for (const field of requiredFields) {
  if (!inputData[field]) {
    throw new Error(`Campo requerido faltante para auditoría: ${field}`);
  }
}

// Validar que changes sea un objeto con before/after
if (!inputData.changes.before || !inputData.changes.after) {
  throw new Error('Los cambios deben incluir estados before y after');
}

// Detectar qué campos específicos cambiaron
const fieldChanges = [];
const beforeState = inputData.changes.before;
const afterState = inputData.changes.after;

// Campos importantes a auditar
const auditableFields = [
  'estado',
  'detalle', 
  'black_alert',
  'sucursal_id',
  'taller_id',
  'asesor_asignado_id',
  'cliente_id',
  'vehiculo_id',
  'vin',
  'tipo_reclamo'
];

auditableFields.forEach(field => {
  if (beforeState[field] !== afterState[field]) {
    fieldChanges.push({
      field: field,
      old_value: beforeState[field],
      new_value: afterState[field],
      change_type: determineChangeType(field, beforeState[field], afterState[field])
    });
  }
});

// Función para determinar tipo de cambio
function determineChangeType(field, oldValue, newValue) {
  if (oldValue === null || oldValue === undefined) {
    return 'created';
  }
  if (newValue === null || newValue === undefined) {
    return 'deleted';
  }
  if (field === 'estado') {
    return 'status_change';
  }
  if (field === 'black_alert') {
    return 'priority_change';
  }
  if (field === 'detalle') {
    return 'content_change';
  }
  return 'updated';
}

// Calcular nivel de criticidad del cambio
let criticalityLevel = 'low';
let requiresNotification = false;

fieldChanges.forEach(change => {
  if (change.field === 'black_alert' && change.new_value === true) {
    criticalityLevel = 'critical';
    requiresNotification = true;
  } else if (change.field === 'estado') {
    if (change.new_value === 'cerrado') {
      criticalityLevel = 'high';
      requiresNotification = true;
    } else {
      criticalityLevel = 'medium';
    }
  } else if (change.field === 'detalle' && change.change_type === 'content_change') {
    criticalityLevel = 'medium';
  }
});

// Estructura de auditoría normalizada
const auditEvent = {
  // Identificadores
  complaint_id: inputData.complaint_id,
  tenant_id: inputData.tenant_id,
  user_id: inputData.user_id,

  // Información del usuario
  user_name: inputData.user_name || 'Sistema',
  user_role: inputData.user_role || 'unknown',
  user_email: inputData.user_email,

  // Cambios detectados
  field_changes: fieldChanges,
  total_changes: fieldChanges.length,

  // Estados completos
  state_before: beforeState,
  state_after: afterState,

  // Metadatos del cambio
  change_timestamp: new Date().toISOString(),
  change_reason: inputData.change_reason || 'No especificado',
  change_notes: inputData.change_notes || '',

  // Análisis de criticidad
  criticality_level: criticalityLevel,
  requires_notification: requiresNotification,

  // Contexto técnico
  source_system: inputData.source_system || 'optimacx_web',
  ip_address: inputData.ip_address,
  user_agent: inputData.user_agent,

  // ID único del evento de auditoría
  audit_id: `audit_${inputData.complaint_id}_${Date.now()}`
};

console.log('Evento de auditoría procesado:', {
  complaint_id: auditEvent.complaint_id,
  tenant_id: auditEvent.tenant_id,
  changes_count: auditEvent.total_changes,
  criticality: auditEvent.criticality_level,
  requires_notification: auditEvent.requires_notification
});

return auditEvent;

