// Generar estadísticas de auditoría y compliance
const auditData = $('Procesar Evento Auditoría').first().json;
const complaintContext = $('Obtener Contexto Reclamo').first().json;
const auditRecord = $('Almacenar Registro Auditoría').first().json;

// Generar resumen del evento de auditoría
const auditSummary = {
  // Identificadores del evento
  audit_id: auditData.audit_id,
  complaint_id: auditData.complaint_id,
  tenant_id: auditData.tenant_id,

  // Información del reclamo
  complaint_context: {
    external_id: complaintContext.id_externo,
    client_name: complaintContext.cliente_nombre,
    vehicle: `${complaintContext.marca} ${complaintContext.modelo}`,
    current_status: complaintContext.estado,
    is_black_alert: complaintContext.black_alert
  },

  // Información del cambio
  change_summary: {
    user_name: auditData.user_name,
    user_role: auditData.user_role,
    total_fields_changed: auditData.total_changes,
    criticality_level: auditData.criticality_level,
    timestamp: auditData.change_timestamp,
    requires_notification: auditData.requires_notification
  },

  // Análisis de los cambios
  change_analysis: {
    status_changes: auditData.field_changes.filter(c => c.field === 'estado').length,
    content_changes: auditData.field_changes.filter(c => c.field === 'detalle').length,
    priority_changes: auditData.field_changes.filter(c => c.field === 'black_alert').length,
    assignment_changes: auditData.field_changes.filter(c => 
      ['asesor_asignado_id', 'sucursal_id', 'taller_id'].includes(c.field)
    ).length,
    structural_changes: auditData.field_changes.filter(c => 
      ['cliente_id', 'vehiculo_id', 'vin'].includes(c.field)
    ).length
  },

  // Métricas de compliance
  compliance_metrics: {
    audit_stored: !!auditRecord.id,
    notification_sent: auditData.requires_notification,
    data_integrity_maintained: true,
    traceability_complete: !!(auditData.user_id && auditData.change_timestamp),
    change_reason_provided: auditData.change_reason !== 'No especificado'
  },

  // Contexto técnico
  technical_context: {
    source_system: auditData.source_system,
    has_ip_tracking: !!auditData.ip_address,
    has_user_agent: !!auditData.user_agent,
    audit_record_id: auditRecord.id
  },

  // Timestamps y metadata
  processing_completed_at: new Date().toISOString(),
  audit_success: true
};

console.log('Auditoría completada:', {
  audit_id: auditSummary.audit_id,
  complaint_id: auditSummary.complaint_id,
  changes: auditSummary.change_summary.total_fields_changed,
  criticality: auditSummary.change_summary.criticality_level,
  notification_sent: auditSummary.compliance_metrics.notification_sent
});

return auditSummary;
