/*** COMPLAINT NOTIFICATIONS - SRP: Solo notificaciones personalizadas por rol ***/
const notificationData = $('Notifications Trigger').first().json;
const currentTarget = $input.first().json;

const complaintData = notificationData.complaint_data;
const aiAnalysis = notificationData.ai_analysis;
const tenantConfig = notificationData.tenant_config;
const isBlackAlert = notificationData.is_black_alert;

// Personalizar notificaci√≥n por rol (siguiendo patr√≥n de encuestas)
let emailSubject, emailContent, priority;

switch (currentTarget.role) {
  case 'jefe_servicio':
    emailSubject = `${isBlackAlert ? 'üö® BLACK ALERT - ' : ''}üìã Nuevo Reclamo ${complaintData.urgencia === 'alta' ? 'URGENTE' : ''} - ID: ${notificationData.complaint_id}`;
    priority = complaintData.urgencia === 'alta' ? 'urgent' : 'high';
    emailContent = `
      <h2>üìã Nuevo Reclamo Asignado</h2>
      ${isBlackAlert ? `
      <div style="background: #fee2e2; border: 2px solid #dc2626; padding: 15px; border-radius: 8px; margin: 15px 0;">
        <h3 style="color: #dc2626;">üö® BLACK ALERT - LEY DEL CONSUMIDOR</h3>
        <p><strong>Este reclamo requiere atenci√≥n inmediata por posible aplicaci√≥n de ley del consumidor</strong></p>
      </div>
      ` : ''}
      <div style="background: #dbeafe; padding: 15px; border-radius: 8px; margin: 15px 0;">
        <h3>üë®‚Äçüíº Como Jefe de Servicio, usted supervisa la resoluci√≥n de este reclamo</h3>
      </div>
    `;
    break;

  case 'asesor_servicio':
    emailSubject = `${isBlackAlert ? 'üö® BLACK ALERT - ' : ''}üìù Reclamo Asignado para Gesti√≥n - ID: ${notificationData.complaint_id}`;
    priority = 'high';
    emailContent = `
      <h2>üìù Reclamo Asignado</h2>
      ${isBlackAlert ? `
      <div style="background: #fee2e2; border: 2px solid #dc2626; padding: 15px; border-radius: 8px; margin: 15px 0;">
        <h3 style="color: #dc2626;">üö® BLACK ALERT - LEY DEL CONSUMIDOR</h3>
        <p><strong>ATENCI√ìN: Este reclamo tiene prioridad legal m√°xima</strong></p>
      </div>
      ` : ''}
      <div style="background: #ecfdf5; padding: 15px; border-radius: 8px; margin: 15px 0;">
        <h3>üë®‚Äçüîß Como Asesor de Servicio, usted es el responsable directo de este reclamo</h3>
      </div>
    `;
    break;

  case 'encargado_calidad':
    emailSubject = `${isBlackAlert ? 'üö® BLACK ALERT - ' : ''}üìä Reclamo para Seguimiento de Calidad - ID: ${notificationData.complaint_id}`;
    priority = isBlackAlert ? 'urgent' : 'normal';
    emailContent = `
      <h2>üìä Seguimiento de Calidad</h2>
      ${isBlackAlert ? `
      <div style="background: #fee2e2; border: 2px solid #dc2626; padding: 15px; border-radius: 8px; margin: 15px 0;">
        <h3 style="color: #dc2626;">üö® BLACK ALERT - LEY DEL CONSUMIDOR</h3>
        <p><strong>Caso cr√≠tico para an√°lisis de calidad y m√©tricas</strong></p>
      </div>
      ` : ''}
      <div style="background: #f0f9ff; padding: 15px; border-radius: 8px; margin: 15px 0;">
        <h3>üìà Como Encargado de Calidad, monitoree este reclamo para m√©tricas y mejoras</h3>
      </div>
    `;
    break;

  default:
    emailSubject = `${isBlackAlert ? 'üö® BLACK ALERT - ' : ''}üìã Nuevo Reclamo - ID: ${notificationData.complaint_id}`;
    priority = 'normal';
    emailContent = `
      <h2>üìã Nuevo Reclamo</h2>
      <div style="background: #f8fafc; padding: 15px; border-radius: 8px; margin: 15px 0;">
        <h3>üìã Reclamo asignado para su conocimiento</h3>
      </div>
    `;
}

// Contenido com√∫n del email (siguiendo patr√≥n de encuestas)
const commonContent = `
  ${emailContent}

  <div style="background: #f8fafc; padding: 20px; border-radius: 8px; margin: 20px 0;">
    <h3>üë§ Informaci√≥n del Cliente</h3>
    <p><strong>Nombre:</strong> ${complaintData.cliente_nombre}</p>
    <p><strong>Tel√©fono:</strong> ${complaintData.cliente_telefono}</p>
    <p><strong>Email:</strong> ${complaintData.cliente_email || 'No proporcionado'}</p>
    <p><strong>Canal:</strong> ${complaintData.canal_origen}</p>
    ${complaintData.vehiculo_patente ? `<p><strong>Veh√≠culo:</strong> ${complaintData.vehiculo_patente}</p>` : ''}
  </div>

  <div style="background: #fffbeb; padding: 20px; border-radius: 8px; margin: 20px 0;">
    <h3>ü§ñ An√°lisis Autom√°tico IA</h3>
    <p><strong>Tipo:</strong> ${complaintData.tipo_reclamo}</p>
    <p><strong>Categor√≠a:</strong> ${complaintData.categoria}</p>
    <p><strong>Urgencia:</strong> ${complaintData.urgencia}</p>
    <p><strong>Sucursal:</strong> ${complaintData.sucursal}</p>
    ${aiAnalysis.analisis?.resumen_problema ? `<p><strong>Resumen:</strong> ${aiAnalysis.analisis.resumen_problema}</p>` : ''}
    ${notificationData.processing_metadata?.used_rag ? '<p>‚úÖ <em>An√°lisis enriquecido con base de conocimiento espec√≠fica</em></p>' : ''}
  </div>

  <div style="background: #f0f9ff; padding: 20px; border-radius: 8px; margin: 20px 0;">
    <h3>üì± Detalle del Reclamo</h3>
    <p style="font-style: italic; background: white; padding: 15px; border-left: 4px solid #2563eb;">
      ${complaintData.detalle}
    </p>
  </div>

  ${aiAnalysis.sugerencias_resolucion ? `
  <div style="background: #f0fdf4; padding: 20px; border-radius: 8px; margin: 20px 0;">
    <h3>üí° Sugerencias de Resoluci√≥n IA</h3>
    <ul>
    ${aiAnalysis.sugerencias_resolucion.map(sug => `
      <li><strong>${sug.accion}</strong> - ${sug.responsable} (${sug.plazo}) - Prioridad: ${sug.prioridad}</li>
    `).join('')}
    </ul>
  </div>
  ` : ''}

  ${aiAnalysis.referencias_politicas && aiAnalysis.referencias_politicas.length > 0 ? `
  <div style="background: #ecfdf5; padding: 20px; border-radius: 8px; margin: 20px 0;">
    <h3>üìö Referencias de Pol√≠ticas Aplicables</h3>
    <ul>
    ${aiAnalysis.referencias_politicas.map(ref => `<li>${ref}</li>`).join('')}
    </ul>
  </div>
  ` : ''}

  <p style="text-align: center; margin-top: 30px;">
    <a href="${tenantConfig.dashboard_url || 'https://app.optimacx.com'}/reclamos/${notificationData.complaint_id}" 
       style="background: #2563eb; color: white; padding: 12px 24px; text-decoration: none; border-radius: 6px; font-size: 16px;">
       Ver Reclamo en Plataforma
    </a>
  </p>

  <div style="background: #f3f4f6; padding: 15px; border-radius: 8px; margin: 20px 0; text-align: center;">
    <p style="margin: 0; font-size: 12px; color: #6b7280;">
      Sistema √ìptima-CX - Notificaci√≥n Autom√°tica | ${new Date().toLocaleString('es-CL')}
    </p>
  </div>
`;

return {
  to: currentTarget.user.email,
  subject: emailSubject,
  html: commonContent,
  priority: priority,
  recipient_name: currentTarget.user.nombre,
  recipient_role: currentTarget.role,
  tenant_id: complaintData.concesionario_id,
  complaint_id: notificationData.complaint_id,
  is_black_alert: isBlackAlert
};

// Generar reporte de notificaciones enviadas
const notificationResults = $input.all();
const originalData = $('Notifications Trigger').first().json;

// Contar notificaciones exitosas por rol
const successfulNotifications = notificationResults.filter(result => 
  result.json?.message_id || result.json?.status === 'sent'
);
const failedNotifications = notificationResults.length - successfulNotifications.length;

// Breakdown por rol
const notificationsByRole = {};
notificationResults.forEach(result => {
  const role = result.json?.recipient_role || 'unknown';
  if (!notificationsByRole[role]) {
    notificationsByRole[role] = { sent: 0, failed: 0 };
  }
  if (result.json?.message_id || result.json?.status === 'sent') {
    notificationsByRole[role].sent++;
  } else {
    notificationsByRole[role].failed++;
  }
});

const notificationReport = {
  complaint_id: originalData.complaint_id,
  tenant_id: originalData.complaint_data.concesionario_id,
  notification_type: 'complaint_assignment',

  // Estad√≠sticas generales
  notification_stats: {
    total_targets: originalData.notification_targets.length,
    successful_sends: successfulNotifications.length,
    failed_sends: failedNotifications,
    success_rate: Math.round((successfulNotifications.length / notificationResults.length) * 100)
  },

  // Breakdown por rol  
  by_role: notificationsByRole,

  // Metadatos
  complaint_urgency: originalData.complaint_data.urgencia,
  is_black_alert: originalData.is_black_alert,
  completed_at: new Date().toISOString(),
  status: 'completed'
};

return notificationReport;

parameters.responseBody:
={
  "status": "success",
  "message": "Notificaciones de reclamo enviadas correctamente",
  "notification_data": {
    "complaint_id": "{{ $('Generate Notification Report').first().json.complaint_id }}",
    "notifications_sent": {{ $('Generate Notification Report').first().json.notification_stats.successful_sends }},
    "total_targets": {{ $('Generate Notification Report').first().json.notification_stats.total_targets }},
    "success_rate": "{{ $('Generate Notification Report').first().json.notification_stats.success_rate }}%",
    "by_role": {{ JSON.stringify($('Generate Notification Report').first().json.by_role) }}
  },
  "timestamp": "{{ new Date().toISOString() }}"
}
