/*** SURVEY CALL CENTER ASSIGNMENT - SRP: Solo asignaci贸n equitativa ***/
const assignmentData = $('Call Center Assignment Trigger').first().json;
const callCenterAgents = $('Get Call Center Agents').first().json;
const clientsToAssign = assignmentData.clients_for_call_center;

// Validar que hay agentes disponibles
if (!callCenterAgents || callCenterAgents.length === 0) {
  throw new Error('No hay agentes de Contact Center activos para este concesionario');
}

// Distribuci贸n equitativa (algoritmo round-robin)
const assignments = [];
let agentIndex = 0;

clientsToAssign.forEach((client, index) => {
  const assignedAgent = callCenterAgents[agentIndex];

  assignments.push({
    // Datos del cliente
    cliente_nombre: client.cliente_nombre,
    cliente_telefono: client.cliente_telefono,
    sucursal: client.sucursal,
    tiempo_sin_respuesta: client.tiempo_sin_respuesta,
    fecha_envio_whatsapp: client.fecha_envio_whatsapp,

    // Datos del agente asignado
    agente_id: assignedAgent.id,
    agente_nombre: assignedAgent.nombre,
    agente_email: assignedAgent.email,
    agente_telefono: assignedAgent.telefono,

    // Metadatos de asignaci贸n
    fecha_asignacion: new Date().toISOString(),
    prioridad: client.tiempo_sin_respuesta > 12 ? 'alta' : 'normal', // >12 horas sin respuesta
    tenant_id: assignmentData.tenant_id
  });

  // Rotar al siguiente agente
  agentIndex = (agentIndex + 1) % callCenterAgents.length;
});

// Agrupar por agente para notificaciones eficientes
const assignmentsByAgent = assignments.reduce((acc, assignment) => {
  const agentId = assignment.agente_id;
  if (!acc[agentId]) {
    acc[agentId] = {
      agent_info: {
        id: assignment.agente_id,
        nombre: assignment.agente_nombre,
        email: assignment.agente_email,
        telefono: assignment.agente_telefono
      },
      assigned_clients: []
    };
  }
  acc[agentId].assigned_clients.push(assignment);
  return acc;
}, {});

return {
  tenant_id: assignmentData.tenant_id,
  total_assignments: assignments.length,
  total_agents: callCenterAgents.length,
  assignments_by_agent: assignmentsByAgent,
  assignment_stats: {
    clients_per_agent: Math.round(assignments.length / callCenterAgents.length * 100) / 100,
    high_priority_count: assignments.filter(a => a.prioridad === 'alta').length,
    assignment_timestamp: new Date().toISOString()
  },
  campaign_metadata: assignmentData.campaign_metadata
};

// Generar reporte final de asignaciones
const assignmentData = $('Assign Clients').first().json;
const notificationResults = $input.all();

const finalReport = {
  campaign_id: assignmentData.campaign_metadata?.campaign_id || `campaign_${Date.now()}`,
  tenant_id: assignmentData.tenant_id,
  assignment_summary: {
    total_clients_assigned: assignmentData.total_assignments,
    total_agents: assignmentData.total_agents,
    assignments_per_agent: assignmentData.assignment_stats.clients_per_agent,
    high_priority_clients: assignmentData.assignment_stats.high_priority_count
  },
  notification_results: {
    emails_sent: notificationResults.filter(r => r.json?.message_id).length,
    emails_failed: notificationResults.length - notificationResults.filter(r => r.json?.message_id).length
  },
  completion_timestamp: new Date().toISOString(),
  status: 'completed'
};

return finalReport;

