// SALES SURVEY RESPONSE MONITOR - SRP: Solo monitoreo de respuestas de VENTAS
// Principio SRP: Una sola responsabilidad - monitorear respuestas de encuestas post-compra

const monitorData = $('Trigger Monitor Respuestas Ventas').first().json;
const whatsappSalesSurveys = $('Obtener Encuestas WhatsApp Ventas').first().json;

// Validar datos de entrada específicos para VENTAS
if (!monitorData.tenant_id) {
  throw new Error('tenant_id es requerido para monitoreo de encuestas VENTAS');
}

if (!whatsappSalesSurveys || whatsappSalesSurveys.length === 0) {
  console.log('No hay encuestas de VENTAS por WhatsApp para monitorear');
  return {
    tenant_id: monitorData.tenant_id,
    survey_type: 'VENTAS',
    department: 'VENTAS',
    sales_clients_for_call_center: [],
    monitoring_stats: {
      total_sent: 0,
      total_completed: 0,
      total_pending: 0,
      non_responders_6h: 0,
      response_rate: 0
    },
    campaign_metadata: monitorData.campaign_metadata || {},
    next_workflow: null
  };
}

// Identificar clientes de VENTAS que NO han respondido en las últimas 6 horas
const cutoffTime = new Date(Date.now() - 6 * 60 * 60 * 1000);

const salesNonResponders = whatsappSalesSurveys.filter(survey => {
  // Solo los que siguen en estado 'enviado_whatsapp' (no completado) de VENTAS
  return survey.estado === 'enviado_whatsapp' && 
         new Date(survey.fecha_ultimo_contacto) <= cutoffTime;
});

// Preparar datos específicos de VENTAS para asignación a contact center
const salesClientsForCallCenter = salesNonResponders.map(survey => ({
  cliente_nombre: survey.cliente_nombre,
  cliente_telefono: survey.cliente_telefono,
  vehiculo_modelo: survey.vehiculo_modelo,
  fecha_venta: survey.fecha_venta,
  asesor_ventas_id: survey.asesor_ventas_id,
  sucursal: survey.sucursal,
  fecha_envio_whatsapp: survey.fecha_ultimo_contacto,
  tiempo_sin_respuesta: Math.round((Date.now() - new Date(survey.fecha_ultimo_contacto)) / (1000 * 60 * 60)) // horas
}));

// Estadísticas de monitoreo específicas para VENTAS
const salesMonitoringStats = {
  total_sent: monitorData.sales_clients_sent || whatsappSalesSurveys.length,
  total_completed: whatsappSalesSurveys.filter(s => s.estado === 'completado').length,
  total_pending: whatsappSalesSurveys.filter(s => s.estado === 'enviado_whatsapp').length,
  non_responders_6h: salesNonResponders.length,
  response_rate: whatsappSalesSurveys.length > 0 ? 
    Math.round((whatsappSalesSurveys.filter(s => s.estado === 'completado').length / whatsappSalesSurveys.length) * 100) : 0,
  monitoring_timestamp: new Date().toISOString()
};

console.log('Monitoreo de respuestas VENTAS completado:', {
  tenantId: monitorData.tenant_id,
  tipo: 'VENTAS',
  totalEnviadas: salesMonitoringStats.total_sent,
  completadas: salesMonitoringStats.total_completed,
  sinRespuesta6h: salesMonitoringStats.non_responders_6h,
  tasaRespuesta: `${salesMonitoringStats.response_rate}%`
});

return {
  tenant_id: monitorData.tenant_id,
  survey_type: 'VENTAS',
  department: 'VENTAS',
  sales_clients_for_call_center: salesClientsForCallCenter,
  monitoring_stats: salesMonitoringStats,
  campaign_metadata: {
    campaign_id: monitorData.campaign_id,
    original_upload: monitorData.upload_metadata,
    batch_reports: monitorData.batch_reports,
    survey_type: 'VENTAS'
  },
  next_workflow: salesClientsForCallCenter.length > 0 ? 'asignacion-contact-center-ventas' : null
};

// Generar reporte final de campaña VENTAS
// Principio SRP: Una sola responsabilidad - generar reporte final de monitoreo

const analysisData = $('Analizar Respuestas Ventas').first().json;

const finalSalesReport = {
  campaign_id: analysisData.campaign_metadata.campaign_id,
  tenant_id: analysisData.tenant_id,
  survey_type: 'VENTAS',
  department: 'VENTAS',
  campaign_status: 'monitoring_completed',

  // Métricas finales específicas de VENTAS
  final_sales_metrics: analysisData.monitoring_stats,

  // Decisión tomada específica para VENTAS
  action_taken: analysisData.sales_clients_for_call_center.length > 0 
    ? 'assigned_to_call_center_ventas' 
    : 'sales_campaign_completed_no_followup',

  // Detalles específicos de VENTAS
  sales_clients_for_followup: analysisData.sales_clients_for_call_center.length,
  completion_timestamp: new Date().toISOString(),

  // Metadatos originales de la campaña VENTAS
  original_sales_campaign: analysisData.campaign_metadata,

  // Recomendaciones específicas para VENTAS
  sales_recommendations: {
    improve_whatsapp_messaging: analysisData.monitoring_stats.response_rate < 50,
    review_sales_process: analysisData.monitoring_stats.non_responders_6h > 10,
    follow_up_needed: analysisData.sales_clients_for_call_center.length > 0
  }
};

console.log('Reporte final de monitoreo VENTAS:', {
  tenantId: finalSalesReport.tenant_id,
  tipo: 'VENTAS',
  status: finalSalesReport.campaign_status,
  clientesSeguimiento: finalSalesReport.sales_clients_for_followup
});

return finalSalesReport;

