// Procesar notificaci√≥n de score bajo en encuesta de ventas
const data = $input.all()[0].json;

// Validar que es una notificaci√≥n de score bajo
if (data.average_score > 8) {
  console.log('Score no requiere notificaci√≥n:', data.average_score);
  return [];
}

// Obtener configuraci√≥n del tenant
const tenantConfig = $workflow.settings.tenant_config || {};

const notificationData = {
  tipo: 'encuesta_ventas_score_bajo',
  tenant_id: data.concesionario_id,
  tenant_name: tenantConfig.nombre || 'Concesionario',

  // Datos de la encuesta
  survey_id: data.id,
  cliente_nombre: data.cliente_nombre,
  cliente_telefono: data.cliente_telefono,
  vehiculo_modelo: data.vehiculo_modelo,
  fecha_venta: data.fecha_venta,

  // Scores detallados
  average_score: data.average_score,
  experiencia_venta: data.experiencia_venta,
  satisfaccion_asesor_ventas: data.satisfaccion_asesor_ventas,
  claridad_informacion: data.claridad_informacion,
  recomendacion_venta: data.recomendacion_venta,
  comentario_venta: data.comentario_venta,

  // Asesor responsable
  asesor_ventas_id: data.asesor_ventas_id,

  // Configuraci√≥n de email
  email_config: tenantConfig.email || {},

  timestamp: new Date().toISOString()
};

return [{ json: notificationData }];

// Preparar emails para todos los destinatarios
const notificationData = $('Procesar Datos').first().json;
const recipients = $('Obtener Destinatarios').first().json || [];
const advisor = $('Obtener Asesor Ventas').first().json?.[0];

// Agregar asesor espec√≠fico a la lista si existe
if (advisor && !recipients.find(r => r.id === advisor.id)) {
  recipients.push(advisor);
}

if (recipients.length === 0) {
  console.log('No se encontraron destinatarios para la notificaci√≥n');
  return [];
}

// Generar emails individuales
const emails = recipients.map(recipient => {
  const subject = `üö® ALERTA: Encuesta de Ventas con Baja Satisfacci√≥n - ${notificationData.cliente_nombre}`;

  const body = `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <title>Alerta: Encuesta de Ventas - Score Bajo</title>
</head>
<body style="font-family: Arial, sans-serif; line-height: 1.6; color: #333;">
    <div style="max-width: 600px; margin: 0 auto; padding: 20px;">
        <h2 style="color: #d32f2f; border-bottom: 2px solid #d32f2f; padding-bottom: 10px;">
            üö® Alerta: Encuesta de Ventas con Baja Satisfacci√≥n
        </h2>

        <div style="background-color: #ffebee; padding: 15px; border-radius: 5px; margin: 20px 0;">
            <h3 style="margin-top: 0; color: #d32f2f;">Puntuaci√≥n Promedio: ${notificationData.average_score}/10</h3>
        </div>

        <h3>üìã Detalles del Cliente:</h3>
        <ul>
            <li><strong>Cliente:</strong> ${notificationData.cliente_nombre}</li>
            <li><strong>Tel√©fono:</strong> ${notificationData.cliente_telefono}</li>
            <li><strong>Veh√≠culo:</strong> ${notificationData.vehiculo_modelo}</li>
            <li><strong>Fecha de Venta:</strong> ${notificationData.fecha_venta}</li>
            ${advisor ? `<li><strong>Asesor:</strong> ${advisor.nombre}</li>` : ''}
        </ul>

        <h3>üìä Puntuaciones Detalladas:</h3>
        <ul>
            <li><strong>Experiencia de Venta:</strong> ${notificationData.experiencia_venta}/10</li>
            <li><strong>Satisfacci√≥n con Asesor:</strong> ${notificationData.satisfaccion_asesor_ventas}/10</li>
            <li><strong>Claridad de Informaci√≥n:</strong> ${notificationData.claridad_informacion}/10</li>
            <li><strong>Recomendaci√≥n:</strong> ${notificationData.recomendacion_venta}/10</li>
        </ul>

        ${notificationData.comentario_venta ? `
        <h3>üí¨ Comentario del Cliente:</h3>
        <div style="background-color: #f5f5f5; padding: 15px; border-radius: 5px; font-style: italic;">
            "${notificationData.comentario_venta}"
        </div>
        ` : ''}

        <div style="background-color: #fff3e0; padding: 15px; border-radius: 5px; margin: 20px 0;">
            <h3 style="margin-top: 0; color: #f57c00;">üéØ Acci√≥n Requerida:</h3>
            <p>Esta encuesta requiere <strong>atenci√≥n inmediata</strong> para:</p>
            <ul>
                <li>Contactar al cliente para resolver inquietudes</li>
                <li>Identificar √°reas de mejora en el proceso</li>
                <li>Implementar acciones correctivas</li>
                <li>Seguimiento para mejorar la experiencia</li>
            </ul>
        </div>

        <p style="margin-top: 30px; font-size: 12px; color: #666;">
            <em>Esta alerta fue generada autom√°ticamente por √ìptima-CX el ${new Date(notificationData.timestamp).toLocaleString('es-CL')}.</em>
        </p>
    </div>
</body>
</html>
  `;

  return {
    json: {
      to: recipient.email,
      subject: subject,
      html: body,
      recipient_name: recipient.nombre,
      recipient_role: recipient.role,
      tenant_id: notificationData.tenant_id,
      survey_id: notificationData.survey_id
    }
  };
});

console.log(`Preparando ${emails.length} emails de notificaci√≥n`);
return emails;

