// SALES SURVEY EXCEL PROCESSOR - SRP: Solo procesamiento de datos Excel VENTAS
// Principio SRP: Una sola responsabilidad - procesar datos Excel de clientes post-compra

const webhookData = $input.first().json;
const tenantId = webhookData.tenant_id;
const salesExcelData = webhookData.sales_excel_data;
const uploadedBy = webhookData.uploaded_by || 'sistema';

// Validar estructura mínima específica para VENTAS
if (!Array.isArray(salesExcelData) || salesExcelData.length === 0) {
  throw new Error('Datos de Excel de VENTAS inválidos o vacíos');
}

console.log('Iniciando procesamiento Excel VENTAS:', {
  tenantId: tenantId,
  filas: salesExcelData.length,
  tipo: 'VENTAS'
});

// Normalizar datos específicos para encuestas de VENTAS (post-compra)
const processedSalesClients = salesExcelData.map((row, index) => {
  const salesClient = {
    // Campos base del cliente (común con post-servicio)
    cliente_nombre: row.nombre || row.NOMBRE || row['Nombre Cliente'] || row.cliente || 'Cliente',
    cliente_telefono: (row.telefono || row.TELEFONO || row['Teléfono'] || row.celular || '').toString().replace(/\D/g, ''),
    cliente_email: row.email || row.EMAIL || row['Email'] || null,
    cliente_rut: row.rut || row.RUT || row['RUT'] || null,

    // Campos específicos de VENTAS (diferentes a post-servicio)
    vehiculo_modelo: row.vehiculo || row.VEHICULO || row['Modelo Vehículo'] || row.modelo || 'Vehículo',
    vehiculo_vin: row.vin || row.VIN || row['VIN'] || null,
    fecha_venta: row.fecha_venta || row['Fecha Venta'] || row.fecha_compra || row['Fecha Compra'] || new Date().toISOString().split('T')[0],
    asesor_ventas_nombre: row.asesor_ventas || row.asesor || row.ASESOR || row['Asesor Ventas'] || 'Equipo de Ventas',
    asesor_ventas_id: row.asesor_id || row.asesor_ventas_id || null,
    sucursal: row.sucursal || row.SUCURSAL || row['Sucursal'] || 'Principal',

    // IDs únicos para seguimiento
    concesionario_id: tenantId,
    survey_token: `sales_${tenantId}_${Date.now()}`
  };

  // Validaciones críticas
  if (!salesClient.cliente_telefono || salesClient.cliente_telefono.length < 8) {
    throw new Error(`Fila ${index + 1}: Teléfono inválido (${salesClient.cliente_telefono})`);
  }
  if (!salesClient.vehiculo_vin) {
    throw new Error(`Fila ${index + 1}: VIN del vehículo es requerido`);
  }

  return salesClient;
});

// Resultado siguiendo patrón de workflows existentes
return {
  tenant_id: tenantId,
  sales_clients_excel: processedSalesClients,
  total_clients: processedSalesClients.length,
  upload_metadata: {
    uploaded_by: uploadedBy,
    upload_timestamp: new Date().toISOString(),
    original_filename: webhookData.original_filename || 'unknown'
  },
  processing_stats: {
    total_rows: salesExcelData.length,
    valid_rows: processedSalesClients.length,
    invalid_rows: salesExcelData.length - processedSalesClients.length,
    unique_vehicles: [...new Set(processedSalesClients.map(c => c.vehiculo_modelo))].length,
    unique_advisors: [...new Set(processedSalesClients.map(c => c.asesor_ventas_nombre))].length
  }
};

