parameters.jsCode:
// Validar que venga lead_id numérico y normalizar
const inData = $input.first().json || {};
const lead_id = Number(inData.lead_id);
const valid = Number.isFinite(lead_id) && lead_id > 0;
return [{ json: { ...inData, lead_id, valid } }];

