// Preparar contexto de cÃ¡lculo de mÃ©tricas
return [{
  json: {
    calculation_start: new Date().toISOString(),
    period_days: 7,
    batch_id: `calc_${Date.now()}`,
    alerts_enabled: true
  }
}];

// Procesar y agregar mÃ©tricas de conversiÃ³n con manejo de errores
try {
  const items = $input.all();
  const resultados = [];
  const alertas = [];
  const batchId = $('Preparar CÃ¡lculo').first().json.batch_id;

  // Agrupar por concesionario para anÃ¡lisis consolidado
  const porConcesionario = {};

  for (const item of items) {
    const data = item.json;
    const key = `${data.concesionario_id}_${data.fecha}`;

    if (!porConcesionario[key]) {
      porConcesionario[key] = {
        concesionario_id: data.concesionario_id,
        concesionario_nombre: data.concesionario_nombre || 'Sin nombre',
        fecha: data.fecha,
        canales: [],
        totales: {
          leads: 0,
          contactados: 0,
          calificados: 0,
          convertidos: 0,
          valor_total: 0
        }
      };
    }

    // Agregar datos del canal (con validaciÃ³n)
    porConcesionario[key].canales.push({
      canal: data.canal_origen || 'desconocido',
      total_leads: data.total_leads || 0,
      tasa_contacto: data.tasa_contacto || 0,
      tasa_calificacion: data.tasa_calificacion || 0,
      tasa_conversion: data.tasa_conversion || 0,
      tasa_conversion_total: data.tasa_conversion_total || 0,
      tiempo_respuesta_horas: data.tiempo_respuesta_horas || 0,
      roi_estimado: data.roi_estimado || 0
    });

    // Acumular totales (con validaciÃ³n)
    porConcesionario[key].totales.leads += data.total_leads || 0;
    porConcesionario[key].totales.contactados += data.leads_contactados || 0;
    porConcesionario[key].totales.calificados += data.leads_calificados || 0;
    porConcesionario[key].totales.convertidos += data.leads_convertidos || 0;
    porConcesionario[key].totales.valor_total += data.valor_total_convertido || 0;
  }

  // Procesar cada concesionario
  for (const [key, concesionario] of Object.entries(porConcesionario)) {
    const totales = concesionario.totales;

    // Calcular mÃ©tricas consolidadas con prevenciÃ³n de divisiÃ³n por cero
    const metricas = {
      batch_id: batchId,
      concesionario_id: concesionario.concesionario_id,
      concesionario_nombre: concesionario.concesionario_nombre,
      fecha_calculo: concesionario.fecha,
      periodo: '24h',

      // Totales
      total_leads: totales.leads,
      total_contactados: totales.contactados,
      total_calificados: totales.calificados,
      total_convertidos: totales.convertidos,
      valor_total_ventas: totales.valor_total,

      // Tasas consolidadas con manejo de divisiÃ³n por cero
      tasa_contacto_general: totales.leads > 0 ? Math.round((totales.contactados / totales.leads) * 100 * 100) / 100 : 0,
      tasa_calificacion_general: totales.contactados > 0 ? Math.round((totales.calificados / totales.contactados) * 100 * 100) / 100 : 0,
      tasa_conversion_general: totales.calificados > 0 ? Math.round((totales.convertidos / totales.calificados) * 100 * 100) / 100 : 0,
      tasa_conversion_total: totales.leads > 0 ? Math.round((totales.convertidos / totales.leads) * 100 * 100) / 100 : 0,

      // ROI y valor promedio
      roi_general: totales.leads > 0 ? Math.round((totales.valor_total / (totales.leads * 50)) * 100) / 100 : 0,
      valor_promedio_venta: totales.convertidos > 0 ? Math.round(totales.valor_total / totales.convertidos) : 0,

      // Mejor canal
      mejor_canal: concesionario.canales.reduce((mejor, actual) => 
        actual.tasa_conversion_total > (mejor?.tasa_conversion_total || 0) ? actual : mejor
      , null)?.canal || 'N/A',

      // Detalle por canal
      detalle_canales: concesionario.canales,

      timestamp: new Date().toISOString(),
      status: 'success'
    };

    resultados.push({ json: metricas });

    // Generar alertas para mÃ©tricas crÃ­ticas
    if (metricas.tasa_conversion_total < 5 && metricas.total_leads >= 10) {
      alertas.push({
        tipo: 'conversion_baja',
        concesionario: metricas.concesionario_nombre,
        tasa: metricas.tasa_conversion_total,
        leads: metricas.total_leads,
        fecha: metricas.fecha_calculo
      });
    }

    if (metricas.tasa_contacto_general < 70 && metricas.total_leads >= 5) {
      alertas.push({
        tipo: 'contacto_bajo',
        concesionario: metricas.concesionario_nombre,
        tasa: metricas.tasa_contacto_general,
        leads: metricas.total_leads,
        fecha: metricas.fecha_calculo
      });
    }
  }

  // Guardar alertas en contexto
  if (alertas.length > 0) {
    $execution.set('alertas_conversion', alertas);
  }

  // Guardar estadÃ­sticas del cÃ¡lculo
  $execution.set('calculation_stats', {
    total_concesionarios: resultados.length,
    total_alertas: alertas.length,
    calculation_time: new Date().toISOString()
  });

  return resultados;

} catch (error) {
  return [{
    json: {
      status: 'error',
      message: 'Error al calcular mÃ©tricas de conversiÃ³n',
      error: error.message,
      timestamp: new Date().toISOString()
    }
  }];
}

// Respuesta cuando no hay datos para calcular
return [{
  json: {
    status: 'no_data',
    message: 'No hay datos de leads para calcular mÃ©tricas',
    period_checked: $('Preparar CÃ¡lculo').first().json.period_days,
    timestamp: new Date().toISOString()
  }
}];

// Respuesta de Ã©xito con estadÃ­sticas
const stats = $execution.get('calculation_stats') || {};
return [{
  json: {
    status: 'success',
    message: 'MÃ©tricas de conversiÃ³n calculadas exitosamente',
    stats: {
      concesionarios_procesados: stats.total_concesionarios || 0,
      alertas_generadas: stats.total_alertas || 0,
      calculation_time: stats.calculation_time
    },
    timestamp: new Date().toISOString()
  }
}];

