// Procesar y agregar mÃ©tricas de conversiÃ³n
const items = $input.all();
const resultados = [];
const alertas = [];

// Agrupar por concesionario para anÃ¡lisis consolidado
const porConcesionario = {};

for (const item of items) {
  const data = item.json;
  const key = `${data.concesionario_id}_${data.fecha}`;

  if (!porConcesionario[key]) {
    porConcesionario[key] = {
      concesionario_id: data.concesionario_id,
      concesionario_nombre: data.concesionario_nombre,
      fecha: data.fecha,
      canales: [],
      totales: {
        leads: 0,
        contactados: 0,
        calificados: 0,
        convertidos: 0,
        valor_total: 0
      }
    };
  }

  // Agregar datos del canal
  porConcesionario[key].canales.push({
    canal: data.canal_origen,
    total_leads: data.total_leads,
    tasa_contacto: data.tasa_contacto,
    tasa_calificacion: data.tasa_calificacion,
    tasa_conversion: data.tasa_conversion,
    tasa_conversion_total: data.tasa_conversion_total,
    tiempo_respuesta_horas: data.tiempo_respuesta_horas,
    roi_estimado: data.roi_estimado
  });

  // Acumular totales
  porConcesionario[key].totales.leads += data.total_leads;
  porConcesionario[key].totales.contactados += data.leads_contactados;
  porConcesionario[key].totales.calificados += data.leads_calificados;
  porConcesionario[key].totales.convertidos += data.leads_convertidos;
  porConcesionario[key].totales.valor_total += data.valor_total_convertido;
}

// Procesar cada concesionario
for (const [key, concesionario] of Object.entries(porConcesionario)) {
  const totales = concesionario.totales;

  // Calcular mÃ©tricas consolidadas
  const metricas = {
    concesionario_id: concesionario.concesionario_id,
    concesionario_nombre: concesionario.concesionario_nombre,
    fecha_calculo: concesionario.fecha,
    periodo: '24h',

    // Totales
    total_leads: totales.leads,
    total_contactados: totales.contactados,
    total_calificados: totales.calificados,
    total_convertidos: totales.convertidos,
    valor_total_ventas: totales.valor_total,

    // Tasas consolidadas
    tasa_contacto_general: Math.round((totales.contactados / totales.leads) * 100 * 100) / 100,
    tasa_calificacion_general: Math.round((totales.calificados / totales.contactados) * 100 * 100) / 100,
    tasa_conversion_general: Math.round((totales.convertidos / totales.calificados) * 100 * 100) / 100,
    tasa_conversion_total: Math.round((totales.convertidos / totales.leads) * 100 * 100) / 100,

    // ROI y valor promedio
    roi_general: Math.round((totales.valor_total / (totales.leads * 50)) * 100) / 100,
    valor_promedio_venta: totales.convertidos > 0 ? Math.round(totales.valor_total / totales.convertidos) : 0,

    // Mejor canal
    mejor_canal: concesionario.canales.reduce((mejor, actual) => 
      actual.tasa_conversion_total > (mejor?.tasa_conversion_total || 0) ? actual : mejor
    , null)?.canal || 'N/A',

    // Detalle por canal
    detalle_canales: concesionario.canales,

    timestamp: new Date().toISOString()
  };

  resultados.push({ json: metricas });

  // Generar alertas para mÃ©tricas crÃ­ticas
  if (metricas.tasa_conversion_total < 5 && metricas.total_leads >= 10) {
    alertas.push({
      tipo: 'conversion_baja',
      concesionario: metricas.concesionario_nombre,
      tasa: metricas.tasa_conversion_total,
      leads: metricas.total_leads,
      fecha: metricas.fecha_calculo
    });
  }

  if (metricas.tasa_contacto_general < 70 && metricas.total_leads >= 5) {
    alertas.push({
      tipo: 'contacto_bajo',
      concesionario: metricas.concesionario_nombre,
      tasa: metricas.tasa_contacto_general,
      leads: metricas.total_leads,
      fecha: metricas.fecha_calculo
    });
  }
}

// Guardar alertas en contexto
if (alertas.length > 0) {
  $execution.set('alertas_conversion', alertas);
}

return resultados;

