// Validar payload para env칤o masivo de WhatsApp
const inData = $input.first().json || {};
const required = ['unique_clients_to_contact', 'tenant_id'];
const missing = required.filter(field => !inData[field]);
const valid = missing.length === 0 && Array.isArray(inData.unique_clients_to_contact) && inData.unique_clients_to_contact.length > 0;
return [{ json: { ...inData, valid, missing_fields: missing } }];

// Procesar y personalizar mensajes de prospecci칩n
try {
  const clientData = $('Validar Payload').first().json;
  const whatsappConfig = $input.first().json;
  const clients = clientData.unique_clients_to_contact || [];

  const mensajeTemplate = `춰Hola {nombre}! 游녦

Soy del equipo de ${whatsappConfig.nombre}, especialistas en veh칤culos.

Vimos que podr칤as estar interesado en nuestros veh칤culos. 쯊e gustar칤a recibir informaci칩n sobre nuestras mejores ofertas?

Responde S칈 si te interesa o NO si prefieres que no te contactemos m치s.

춰Esperamos poder ayudarte a encontrar tu veh칤culo ideal! 游뚱九`;

  const mensajes = [];

  for (let i = 0; i < clients.length; i++) {
    const client = clients[i];

    if (!client.telefono || !client.nombre) {
      continue;
    }

    const nombreCliente = client.nombre || 'Estimado cliente';
    const mensajePersonalizado = mensajeTemplate.replace('{nombre}', nombreCliente);

    mensajes.push({
      cliente_nombre: nombreCliente,
      cliente_telefono: client.telefono,
      mensaje_personalizado: mensajePersonalizado,
      concesionario_id: whatsappConfig.concesionario_id,
      concesionario_nombre: whatsappConfig.nombre,
      whatsapp_token: whatsappConfig.whatsapp_business_token,
      whatsapp_phone_id: whatsappConfig.whatsapp_phone_id,
      index: i,
      total: clients.length,
      delay_seconds: Math.floor(i / 5) * 12,
      timestamp: new Date().toISOString()
    });
  }

  return mensajes.map(msg => ({ json: msg }));

} catch (error) {
  return [{
    json: {
      status: 'error',
      message: 'Error al procesar mensajes de prospecci칩n',
      error: error.message,
      timestamp: new Date().toISOString()
    }
  }];
}

