// Procesar webhook de respuesta desde Chatwoot/WhatsApp
const payload = $input.first().json;

// Extraer datos del webhook
const webhookData = {
  // Datos de Chatwoot
  account_id: payload.account?.id,
  conversation_id: payload.conversation?.id,
  message_id: payload.id,

  // Datos del mensaje
  content: payload.content || '',
  message_type: payload.message_type,
  created_at: payload.created_at,

  // Datos del contacto/cliente
  contact_phone: payload.conversation?.contact_inbox?.source_id?.replace(/\D/g, ''),
  contact_name: payload.sender?.name || payload.conversation?.contact_inbox?.name,

  // Metadatos
  inbox_id: payload.conversation?.inbox_id,
  source_platform: 'whatsapp'
};

// Validaciones bÃ¡sicas
if (!webhookData.account_id) {
  throw new Error('Account ID requerido para identificar concesionario');
}

if (!webhookData.contact_phone) {
  throw new Error('NÃºmero de telÃ©fono requerido para identificar lead');
}

if (!webhookData.content || webhookData.content.trim() === '') {
  throw new Error('Mensaje vacÃ­o - no procesar');
}

// Solo procesar mensajes entrantes del cliente
if (webhookData.message_type !== 'incoming') {
  throw new Error('Solo procesar mensajes entrantes');
}

return [{ json: webhookData }];

// Procesar respuesta de Gemini y preparar datos para flujo
const webhookData = $('Procesar Webhook').item.json;
const leadData = $('Identificar Lead y Concesionario').item.json;
const geminiResponse = $input.first().json;

// Extraer anÃ¡lisis de IA
let analisisIA;
try {
  const content = geminiResponse.candidates[0].content.parts[0].text;
  // Limpiar la respuesta para extraer solo el JSON
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    analisisIA = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No se encontrÃ³ JSON vÃ¡lido en respuesta de IA');
  }
} catch (error) {
  // Fallback si falla el parsing
  analisisIA = {
    intencion: 'neutral',
    nivel_interes: 5,
    accion_recomendada: 'seguimiento_programado',
    preguntas_cliente: [],
    presupuesto_mencionado: null,
    urgencia_compra: 'no_especificada',
    observaciones: 'Error en anÃ¡lisis de IA: ' + error.message,
    requiere_escalamiento: false
  };
}

// Determinar nuevo estado del lead
let nuevoEstado = 'respondido';
if (analisisIA.intencion === 'positivo' && analisisIA.nivel_interes >= 7) {
  nuevoEstado = 'caliente';
} else if (analisisIA.intencion === 'negativo') {
  nuevoEstado = 'perdido';
} else if (analisisIA.intencion === 'consulta') {
  nuevoEstado = 'en_seguimiento';
}

// Calcular prioridad
let prioridad = 'media';
if (analisisIA.nivel_interes >= 8 || analisisIA.urgencia_compra === 'inmediata') {
  prioridad = 'alta';
} else if (analisisIA.nivel_interes <= 3 || analisisIA.intencion === 'negativo') {
  prioridad = 'baja';
}

const resultado = {
  // Datos del webhook y lead
  lead_id: leadData.lead_id,
  concesionario_id: leadData.concesionario_id,
  telefono_cliente: leadData.telefono_cliente,
  nombre_cliente: leadData.nombre_cliente,
  asesor_asignado_id: leadData.asesor_asignado_id,
  asesor_nombre: leadData.asesor_nombre,
  asesor_email: leadData.asesor_email,

  // Datos del mensaje
  mensaje_cliente: webhookData.content,
  conversation_id: webhookData.conversation_id,
  message_id: webhookData.message_id,

  // AnÃ¡lisis de IA
  analisis_ia: analisisIA,

  // Estados calculados
  nuevo_estado: nuevoEstado,
  prioridad: prioridad,

  // Metadatos
  timestamp: new Date().toISOString(),
  requiere_notificacion: analisisIA.requiere_escalamiento || prioridad === 'alta',
  accion_siguiente: analisisIA.accion_recomendada
};

return [{ json: resultado }];

