// Procesar carga de archivo Excel con validaciones de concesionario
const payload = $input.first().json;

// Validar estructura del payload
if (!payload.concesionario_id) {
  throw new Error('concesionario_id es requerido');
}

if (!payload.archivo_excel) {
  throw new Error('archivo_excel es requerido');
}

if (!payload.usuario_carga_id) {
  throw new Error('usuario_carga_id es requerido');
}

// Validar permisos del usuario
const rolesPermitidos = ['jefe_ventas', 'admin', 'gerencia'];
if (!payload.usuario_rol || !rolesPermitidos.includes(payload.usuario_rol)) {
  throw new Error(`Rol ${payload.usuario_rol} no autorizado para carga masiva`);
}

const metadata = {
  concesionario_id: payload.concesionario_id,
  usuario_carga_id: payload.usuario_carga_id,
  usuario_rol: payload.usuario_rol,
  archivo_nombre: payload.archivo_nombre || 'leads_import.xlsx',
  archivo_excel: payload.archivo_excel, // Base64 encoded
  timestamp_carga: new Date().toISOString(),
  validaciones: {
    estructura_requerida: ['nombre', 'telefono', 'email', 'modelo_interes'],
    campos_opcionales: ['observaciones', 'presupuesto', 'urgencia']
  }
};

return [{ json: metadata }];

// Procesar archivo Excel y extraer datos de leads
const uploadData = $('Validar Carga').item.json;
const permissionsData = $input.first().json;

// Simular procesamiento de Excel (en producci√≥n usar√≠as una librer√≠a como xlsx)
// Para este ejemplo, asumimos que el archivo ya viene procesado como JSON
const excelBase64 = uploadData.archivo_excel;

// Datos de ejemplo que se extraer√≠an del Excel
const leadsSample = [
  {
    nombre: "Juan P√©rez",
    telefono: "+56912345678",
    email: "juan.perez@email.com",
    modelo_interes: "Toyota Corolla",
    observaciones: "Cliente referido",
    presupuesto: "15000000",
    urgencia: "alta"
  },
  {
    nombre: "Mar√≠a Gonz√°lez",
    telefono: "+56987654321",
    email: "maria.gonzalez@email.com",
    modelo_interes: "Honda Civic",
    observaciones: "Busca financiamiento",
    presupuesto: "12000000",
    urgencia: "media"
  }
];

// En producci√≥n, aqu√≠ procesar√≠as el Excel real:
// const XLSX = require('xlsx');
// const buffer = Buffer.from(excelBase64, 'base64');
// const workbook = XLSX.read(buffer, {type: 'buffer'});
// const worksheet = workbook.Sheets[workbook.SheetNames[0]];
// const leadsData = XLSX.utils.sheet_to_json(worksheet);

const leadsData = leadsSample; // Usar datos de ejemplo

// Validar y limpiar datos
const leadsValidados = [];
const errores = [];

for (let i = 0; i < leadsData.length; i++) {
  const lead = leadsData[i];
  const erroresLead = [];

  // Validaciones b√°sicas
  if (!lead.nombre || lead.nombre.trim() === '') {
    erroresLead.push('Nombre es requerido');
  }

  if (!lead.telefono || lead.telefono.trim() === '') {
    erroresLead.push('Tel√©fono es requerido');
  } else {
    // Limpiar formato de tel√©fono
    lead.telefono = lead.telefono.replace(/\D/g, '');
    if (lead.telefono.length < 8) {
      erroresLead.push('Tel√©fono debe tener al menos 8 d√≠gitos');
    }
    // Agregar prefijo +56 si no lo tiene
    if (!lead.telefono.startsWith('56')) {
      lead.telefono = '56' + lead.telefono;
    }
    lead.telefono = '+' + lead.telefono;
  }

  if (!lead.email || !lead.email.includes('@')) {
    erroresLead.push('Email inv√°lido');
  }

  if (!lead.modelo_interes || lead.modelo_interes.trim() === '') {
    erroresLead.push('Modelo de inter√©s es requerido');
  }

  if (erroresLead.length > 0) {
    errores.push({
      fila: i + 2, // +2 porque Excel empieza en 1 y hay header
      datos: lead,
      errores: erroresLead
    });
  } else {
    // Agregar datos del concesionario
    lead.concesionario_id = uploadData.concesionario_id;
    lead.fuente_lead = 'excel_masivo';
    lead.estado = 'pendiente_validacion';
    lead.usuario_carga_id = uploadData.usuario_carga_id;
    lead.fecha_carga = uploadData.timestamp_carga;

    leadsValidados.push(lead);
  }
}

const resultado = {
  concesionario_id: uploadData.concesionario_id,
  concesionario_nombre: permissionsData.concesionario_nombre,
  usuario_carga: permissionsData.usuario_nombre,
  archivo_nombre: uploadData.archivo_nombre,
  timestamp_procesamiento: new Date().toISOString(),

  // Estad√≠sticas
  total_filas_procesadas: leadsData.length,
  leads_validos: leadsValidados.length,
  leads_con_errores: errores.length,

  // Datos
  leads_validados: leadsValidados,
  errores_validacion: errores,

  // Estado del procesamiento
  procesamiento_exitoso: errores.length === 0 || (errores.length / leadsData.length) < 0.5
};

return [{ json: resultado }];

// Filtrar leads √∫nicos y preparar para inserci√≥n
const processingData = $('Procesar Excel').item.json;
const duplicateCheck = $input.all();

// Crear mapa de duplicados
const duplicadosMap = new Map();
for (const item of duplicateCheck) {
  const data = item.json;
  if (data.status === 'duplicado') {
    duplicadosMap.set(data.telefono_nuevo, {
      lead_existente_id: data.lead_existente_id,
      estado_existente: data.estado_existente
    });
  }
}

// Filtrar leads √∫nicos
const leadsUnicos = [];
const leadsDuplicados = [];

for (const lead of processingData.leads_validados) {
  if (duplicadosMap.has(lead.telefono)) {
    leadsDuplicados.push({
      ...lead,
      motivo_rechazo: 'Lead duplicado',
      lead_existente_id: duplicadosMap.get(lead.telefono).lead_existente_id
    });
  } else {
    leadsUnicos.push(lead);
  }
}

const resultado = {
  ...processingData,
  leads_unicos: leadsUnicos,
  leads_duplicados: leadsDuplicados,
  total_unicos: leadsUnicos.length,
  total_duplicados: leadsDuplicados.length,
  listo_para_insercion: leadsUnicos.length > 0
};

return [{ json: resultado }];

