// Procesar y clasificar cotizaciones para seguimiento
const quotationData = $input.all();

if (!quotationData || quotationData.length === 0) {
  return [];
}

// Agrupar por tipo de seguimiento y prioridad
const processedQuotations = quotationData.map(item => {
  const quotation = item.json;

  // Skip si no necesita seguimiento
  if (quotation.tipo_seguimiento === 'no_seguimiento') {
    return null;
  }

  // Determinar estrategia de contacto
  let contactStrategy = [];
  let messageTemplate = '';
  let urgencyLevel = 'normal';

  switch (quotation.tipo_seguimiento) {
    case 'seguimiento_4h':
      contactStrategy = ['whatsapp', 'email'];
      messageTemplate = 'initial_followup';
      urgencyLevel = 'alta';
      break;

    case 'seguimiento_1_3d':
      contactStrategy = ['whatsapp', 'call'];
      messageTemplate = 'early_followup';
      urgencyLevel = 'alta';
      break;

    case 'seguimiento_4_7d':
      contactStrategy = ['call', 'whatsapp'];
      messageTemplate = 'persistent_followup';
      urgencyLevel = 'media';
      break;

    case 'seguimiento_8_15d':
      contactStrategy = ['email', 'whatsapp'];
      messageTemplate = 'retention_attempt';
      urgencyLevel = 'media';
      break;

    case 'seguimiento_16_30d':
      contactStrategy = ['call'];
      messageTemplate = 'final_attempt';
      urgencyLevel = 'baja';
      break;

    case 'perdido_definitivo':
      contactStrategy = [];
      messageTemplate = 'mark_as_lost';
      urgencyLevel = 'archivado';
      break;

    default:
      return null;
  }

  // Personalizar mensaje según contexto
  const contextualData = {
    cliente_nombre: quotation.nombre_cliente,
    modelo_interes: quotation.modelo_interes,
    monto_cotizacion: new Intl.NumberFormat('es-CL', {
      style: 'currency',
      currency: 'CLP'
    }).format(quotation.monto_cotizacion),
    dias_transcurridos: Math.floor(quotation.dias_desde_cotizacion),
    asesor_nombre: quotation.asesor_nombre,
    concesionario_nombre: quotation.concesionario_nombre
  };

  return {
    json: {
      ...quotation,
      contact_strategy: contactStrategy,
      message_template: messageTemplate,
      urgency_level: urgencyLevel,
      contextual_data: contextualData,
      follow_up_id: `${quotation.concesionario_id}_${quotation.id}_${Date.now()}`,
      should_notify_supervisor: quotation.prioridad_seguimiento === 'muy_alta' && quotation.seguimientos_realizados >= 2
    }
  };
}).filter(item => item !== null);

console.log('Cotizaciones procesadas para seguimiento:', {
  total_cotizaciones: processedQuotations.length,
  por_tipo: processedQuotations.reduce((acc, item) => {
    const tipo = item.json.tipo_seguimiento;
    acc[tipo] = (acc[tipo] || 0) + 1;
    return acc;
  }, {}),
  alta_prioridad: processedQuotations.filter(item => item.json.prioridad_seguimiento === 'muy_alta').length
});

return processedQuotations;

// Generar mensaje personalizado según template y contexto
const quotationData = $input.first().json;
const { message_template, contextual_data, tipo_seguimiento } = quotationData;

// Templates de mensajes por tipo de seguimiento
const messageTemplates = {
  initial_followup: {
    whatsapp: `Hola ${contextual_data.cliente_nombre} 👋

Soy ${contextual_data.asesor_nombre} de ${contextual_data.concesionario_nombre}.

Te enviamos la cotización del ${contextual_data.modelo_interes} por ${contextual_data.monto_cotizacion}.

¿Tuviste oportunidad de revisarla? ¿Tienes alguna consulta?

¡Estamos aquí para ayudarte! 🚗`,

    email: {
      subject: `Seguimiento Cotización ${contextual_data.modelo_interes} - ${contextual_data.concesionario_nombre}`,
      body: `Estimado/a ${contextual_data.cliente_nombre},

Espero que se encuentre muy bien.

Le escribo para hacer seguimiento a la cotización del ${contextual_data.modelo_interes} que le enviamos por ${contextual_data.monto_cotizacion}.

¿Ha tenido oportunidad de revisar la propuesta? ¿Hay algún aspecto que le gustaría conversar o aclarar?

Quedo atento a sus comentarios y disponible para coordinar una reunión cuando guste.

Saludos cordiales,
${contextual_data.asesor_nombre}
${contextual_data.concesionario_nombre}`
    }
  },

  early_followup: {
    whatsapp: `Hola ${contextual_data.cliente_nombre} 🙋‍♂️

Han pasado ${contextual_data.dias_transcurridos} días desde que te cotizamos el ${contextual_data.modelo_interes}.

¿Cómo vas con la decisión? ¿Hay algo específico que te esté frenando?

Podemos revisar la cotización juntos y ver si hay algún ajuste que podamos hacer 💪

¿Te parece si conversamos hoy?`,

    call_script: `Buenos días/tardes ${contextual_data.cliente_nombre}, habla ${contextual_data.asesor_nombre} de ${contextual_data.concesionario_nombre}. Le llamo para hacer seguimiento a la cotización del ${contextual_data.modelo_interes} que le envié hace ${contextual_data.dias_transcurridos} días. ¿Ha podido revisarla? ¿Hay alguna duda que pueda aclararle?`
  },

  persistent_followup: {
    whatsapp: `${contextual_data.cliente_nombre}, ¡espero que esté muy bien! 😊

Veo que aún no hemos podido concretar lo del ${contextual_data.modelo_interes}.

¿Hay algo que no le haya quedado claro de la propuesta?

Recuerde que tenemos facilidades de financiamiento y podemos revisar las condiciones.

¿Le parece si hablamos por teléfono esta semana?`,

    call_script: `${contextual_data.cliente_nombre}, soy ${contextual_data.asesor_nombre}. Quería saber cómo va su proceso de decisión sobre el ${contextual_data.modelo_interes}. Han pasado ${contextual_data.dias_transcurridos} días y me gustaría saber si hay algo más que pueda hacer para ayudarle. ¿Podríamos agendar una cita para conversar en persona?`
  },

  retention_attempt: {
    email: {
      subject: `¡No queremos perderte! - Última oportunidad ${contextual_data.modelo_interes}`,
      body: `Estimado/a ${contextual_data.cliente_nombre},

Notamos que aún no hemos podido concretar la compra del ${contextual_data.modelo_interes}.

Como valoramos mucho su interés, queremos ofrecerle condiciones especiales:

• Revisión de financiamiento
• Descuentos adicionales disponibles
• Facilidades de pago

Esta oferta es válida por tiempo limitado.

¿Le interesa que conversemos una vez más?

Quedo a su disposición,
${contextual_data.asesor_nombre}`
    },

    whatsapp: `${contextual_data.cliente_nombre}, no queremos que se nos escape 😅

Sé que han pasado ${contextual_data.dias_transcurridos} días desde la cotización del ${contextual_data.modelo_interes}.

¿Qué tal si le ofrezco condiciones especiales? Podemos revisar:

✅ Mejor precio
✅ Facilidades de pago
✅ Descuentos adicionales

¿Le parece que conversemos mañana?`
  },

  final_attempt: {
    call_script: `${contextual_data.cliente_nombre}, soy ${contextual_data.asesor_nombre} de ${contextual_data.concesionario_nombre}. Esta será mi última llamada sobre el ${contextual_data.modelo_interes}. Han pasado ${contextual_data.dias_transcurridos} días y entiendo que quizás ya no esté interesado. Si aún le interesa, me gustaría hacer una última propuesta. Si no, respeto completamente su decisión.`
  }
};

// Seleccionar mensajes según template
const selectedMessages = messageTemplates[message_template] || {};

// Preparar output con mensajes personalizados
const messageOutput = {
  ...quotationData,
  personalized_messages: selectedMessages,
  generated_at: new Date().toISOString()
};

console.log('Mensajes generados para seguimiento:', {
  lead_id: quotationData.id,
  template: message_template,
  strategies: quotationData.contact_strategy,
  urgency: quotationData.urgency_level
});

return messageOutput;

// Generar métricas de seguimientos procesados
const processedData = $input.all();

const summary = {
  total_seguimientos_realizados: processedData.length,
  por_tipo: processedData.reduce((acc, item) => {
    const tipo = item.json.tipo_seguimiento;
    acc[tipo] = (acc[tipo] || 0) + 1;
    return acc;
  }, {}),
  por_prioridad: processedData.reduce((acc, item) => {
    const prioridad = item.json.prioridad_seguimiento;
    acc[prioridad] = (acc[prioridad] || 0) + 1;
    return acc;
  }, {}),
  supervisores_notificados: processedData.filter(item => 
    item.json.should_notify_supervisor
  ).length,
  cotizaciones_perdidas: processedData.filter(item => 
    item.json.urgency_level === 'archivado'
  ).length,
  valor_total_seguimiento: processedData.reduce((sum, item) => 
    sum + (item.json.monto_cotizacion || 0), 0
  ),
  timestamp: new Date().toISOString(),
  execution_success: true
};

console.log('Seguimientos procesados:', summary);

return [{ json: summary }];

// No hay cotizaciones que requieran seguimiento
console.log('No hay cotizaciones que requieran seguimiento en este momento');

return [{
  json: {
    message: 'No hay cotizaciones pendientes de seguimiento',
    timestamp: new Date().toISOString(),
    execution_success: true,
    quotations_found: 0
  }
}];
