// Validar configuraci贸n de seguimiento de cotizaciones
try {
  const configuracion = {
    intervalos_horas: 4,
    max_seguimientos_batch: 25,
    politicas_seguimiento: {
      seguimiento_regular: {
        tiempo_limite_horas: 24,
        frecuencia_horas: 48,
        canal_preferido: 'whatsapp'
      },
      proximo_vencimiento: {
        tiempo_limite_dias: 2,
        frecuencia_horas: 24,
        canal_preferido: 'whatsapp',
        urgencia: 'alta'
      },
      vencimiento_urgente: {
        tiempo_limite_dias: 1,
        frecuencia_horas: 12,
        canal_preferido: 'llamada',
        urgencia: 'critica'
      },
      seguimiento_prioritario: {
        tiempo_limite_horas: 12,
        frecuencia_horas: 12,
        canal_preferido: 'llamada',
        solo_alta_prioridad: true
      },
      sin_respuesta: {
        tiempo_limite_horas: 72,
        frecuencia_horas: 48,
        canal_preferido: 'whatsapp'
      }
    },
    ventana_analisis_dias: 30,
    canales_disponibles: ['whatsapp', 'llamada', 'email'],
    templates_mensaje: {
      vencimiento_cotizacion: 'urgente',
      proximo_vencimiento: 'recordatorio',
      sin_respuesta_72h: 'seguimiento',
      seguimiento_prioritario: 'prioritario',
      seguimiento_regular: 'regular'
    },
    timestamp: new Date().toISOString()
  };

  return [{
    json: {
      configuracion_valida: true,
      configuracion,
      estado: 'inicializado'
    }
  }];

} catch (error) {
  return [{
    json: {
      configuracion_valida: false,
      error: error.message,
      estado: 'error_configuracion',
      timestamp: new Date().toISOString()
    }
  }];
}

// Procesar cotizaciones y determinar estrategia de seguimiento
try {
  const items = $input.all();
  const configuracion = $('Validar Configuraci贸n').first().json.configuracion;
  const seguimientos = [];

  for (const item of items) {
    const data = item.json;

    // Validar datos esenciales
    if (!data.cotizacion_id || !data.nombre_cliente || !data.telefono_cliente) {
      console.log(`Datos incompletos para cotizaci贸n: ${data.cotizacion_id}`);
      continue;
    }

    // Determinar tipo de seguimiento basado en el contexto
    let tipoSeguimiento = 'seguimiento_regular';
    let urgencia = 'media';
    let mensajeTemplate = 'seguimiento_regular';
    let canalPreferido = 'whatsapp';

    const diasRestantes = Math.ceil(data.dias_para_vencimiento || 0);
    const horasCreacion = data.horas_desde_creacion || 0;

    if (diasRestantes <= 0) {
      tipoSeguimiento = 'vencimiento_urgente';
      urgencia = 'critica';
      mensajeTemplate = 'vencimiento_cotizacion';
      canalPreferido = 'llamada';
    } else if (diasRestantes <= 1) {
      tipoSeguimiento = 'vencimiento_urgente';
      urgencia = 'critica';
      mensajeTemplate = 'vencimiento_cotizacion';
      canalPreferido = 'llamada';
    } else if (diasRestantes <= 2) {
      tipoSeguimiento = 'proximo_vencimiento';
      urgencia = 'alta';
      mensajeTemplate = 'proximo_vencimiento';
      canalPreferido = 'whatsapp';
    } else if (horasCreacion >= 72 && data.total_seguimientos === 0) {
      tipoSeguimiento = 'sin_respuesta';
      urgencia = 'media';
      mensajeTemplate = 'sin_respuesta_72h';
      canalPreferido = 'whatsapp';
    } else if (data.prioridad_seguimiento === 'alta') {
      tipoSeguimiento = 'seguimiento_prioritario';
      urgencia = 'alta';
      mensajeTemplate = 'seguimiento_prioritario';
      canalPreferido = 'llamada';
    }

    // Formatear monto para mostrar
    const montoFormateado = data.monto_total ? 
      new Intl.NumberFormat('es-CL', { style: 'currency', currency: 'CLP' }).format(data.monto_total) :
      'Monto no especificado';

    const seguimiento = {
      // Identificadores
      cotizacion_id: data.cotizacion_id,
      lead_id: data.lead_id,
      numero_cotizacion: data.numero_cotizacion || `COT-${data.cotizacion_id}`,

      // Datos del cliente
      cliente_nombre: data.nombre_cliente,
      cliente_telefono: data.telefono_cliente,
      cliente_email: data.email_cliente,

      // Datos del asesor
      asesor_id: data.asesor_asignado_id,
      asesor_nombre: data.asesor_nombre || 'Asesor sin nombre',
      asesor_telefono: data.asesor_telefono,
      asesor_email: data.asesor_email,

      // Datos del concesionario
      concesionario_id: data.concesionario_id,
      concesionario_nombre: data.concesionario_nombre || 'Concesionario no identificado',

      // Datos de la cotizaci贸n
      monto_total: data.monto_total,
      monto_formateado: montoFormateado,
      vehiculo_modelo: data.vehiculo_modelo || 'Veh铆culo no especificado',
      vehiculo_marca: data.vehiculo_marca || '',
      fecha_creacion: data.fecha_creacion,
      fecha_vencimiento: data.fecha_vencimiento,
      estado_cotizacion: data.estado,

      // An谩lisis de tiempo
      dias_restantes: diasRestantes,
      horas_desde_creacion: Math.floor(horasCreacion),
      total_seguimientos_previos: data.total_seguimientos || 0,
      ultimo_seguimiento: data.ultimo_seguimiento,

      // Estrategia de seguimiento
      tipo_seguimiento: tipoSeguimiento,
      urgencia: urgencia,
      mensaje_template: mensajeTemplate,
      canal_preferido: canalPreferido,

      // Flags de acci贸n
      requiere_notificacion_asesor: diasRestantes <= 2,
      requiere_escalamiento: diasRestantes <= 0 || urgencia === 'critica',
      requiere_llamada: canalPreferido === 'llamada',
      requiere_supervisor: urgencia === 'critica' && data.total_seguimientos >= 2,

      // Metadatos
      timestamp: new Date().toISOString(),
      procesado: true,
      errores: []
    };

    seguimientos.push({ json: seguimiento });
  }

  if (seguimientos.length === 0) {
    return [{
      json: {
        procesado: false,
        message: 'No hay seguimientos v谩lidos para procesar',
        total_items: items.length,
        timestamp: new Date().toISOString()
      }
    }];
  }

  return seguimientos;

} catch (error) {
  return [{
    json: {
      status: 'error',
      message: 'Error al procesar seguimientos de cotizaciones',
      error: error.message,
      timestamp: new Date().toISOString()
    }
  }];
}

