// Generar resumen de seguimientos procesados
try {
  const seguimientos = $input.all();

  const resumen = {
    total_seguimientos: seguimientos.length,
    por_tipo: {},
    por_urgencia: {},
    por_canal: {},
    escalados: 0,
    whatsapp_enviados: 0,
    llamadas_programadas: 0,
    cotizaciones_urgentes: 0,
    errores: 0,
    timestamp: new Date().toISOString(),
    estado: 'completado'
  };

  for (const seguimiento of seguimientos) {
    const data = seguimiento.json;

    // Verificar si es respuesta de error
    if (data.status === 'error') {
      resumen.errores++;
      continue;
    }

    // Contar por tipo
    resumen.por_tipo[data.tipo_seguimiento] = (resumen.por_tipo[data.tipo_seguimiento] || 0) + 1;

    // Contar por urgencia
    resumen.por_urgencia[data.urgencia] = (resumen.por_urgencia[data.urgencia] || 0) + 1;

    // Contar por canal
    resumen.por_canal[data.canal_usado] = (resumen.por_canal[data.canal_usado] || 0) + 1;

    // Contar especiales
    if (data.requiere_escalamiento) resumen.escalados++;
    if (data.canal_usado === 'whatsapp') resumen.whatsapp_enviados++;
    if (data.canal_usado === 'llamada') resumen.llamadas_programadas++;
    if (data.urgencia === 'critica') resumen.cotizaciones_urgentes++;
  }

  return [{ json: resumen }];

} catch (error) {
  return [{
    json: {
      status: 'error',
      message: 'Error al generar resumen',
      error: error.message,
      timestamp: new Date().toISOString()
    }
  }];
}

// Respuesta de error por configuraci贸n inv谩lida
return [{
  json: {
    status: 'error',
    message: 'Configuraci贸n de seguimiento de cotizaciones inv谩lida',
    error_details: $json,
    timestamp: new Date().toISOString()
  }
}];

// Respuesta cuando no hay cotizaciones para seguimiento
return [{
  json: {
    status: 'success',
    message: 'No hay cotizaciones pendientes de seguimiento',
    total_cotizaciones_revisadas: 0,
    timestamp: new Date().toISOString()
  }
}];

// Respuesta de error en procesamiento
return [{
  json: {
    status: 'error',
    message: 'Error en el procesamiento de seguimientos',
    error_details: $json,
    timestamp: new Date().toISOString()
  }
}];
