// Analizar m√©tricas de performance y detectar anomal√≠as
const executions = $input.all()[0].json.data || [];
const recentMetrics = $input.all()[1].json || [];

const now = new Date();
const oneHourAgo = new Date(now.getTime() - 60 * 60 * 1000);

// Filtrar ejecuciones de la √∫ltima hora
const recentExecutions = executions.filter(exec => {
  const execTime = new Date(exec.startedAt);
  return execTime >= oneHourAgo;
});

// Analizar m√©tricas por workflow
const workflowMetrics = {};
const alerts = [];

recentExecutions.forEach(exec => {
  const workflowId = exec.workflowId;
  const duration = exec.stoppedAt ? 
    new Date(exec.stoppedAt).getTime() - new Date(exec.startedAt).getTime() : null;
  const status = exec.finished ? 'success' : 'failed';

  if (!workflowMetrics[workflowId]) {
    workflowMetrics[workflowId] = {
      workflow_id: workflowId,
      workflow_name: exec.workflowData?.name || 'Unknown',
      total_executions: 0,
      successful_executions: 0,
      failed_executions: 0,
      avg_duration: 0,
      max_duration: 0,
      min_duration: Infinity,
      durations: [],
      tenant_id: $workflow.settings.tenant_id
    };
  }

  const metrics = workflowMetrics[workflowId];
  metrics.total_executions++;

  if (status === 'success') {
    metrics.successful_executions++;
  } else {
    metrics.failed_executions++;
  }

  if (duration !== null) {
    metrics.durations.push(duration);
    metrics.max_duration = Math.max(metrics.max_duration, duration);
    metrics.min_duration = Math.min(metrics.min_duration, duration);
  }
});

// Calcular promedios y detectar anomal√≠as
Object.values(workflowMetrics).forEach(metrics => {
  if (metrics.durations.length > 0) {
    metrics.avg_duration = metrics.durations.reduce((a, b) => a + b, 0) / metrics.durations.length;
  }

  // Detectar anomal√≠as
  const failureRate = metrics.failed_executions / metrics.total_executions;
  const avgDurationSeconds = metrics.avg_duration / 1000;

  // Alert: Tasa de fallos > 20%
  if (failureRate > 0.2) {
    alerts.push({
      type: 'high_failure_rate',
      severity: 'critical',
      workflow_id: metrics.workflow_id,
      workflow_name: metrics.workflow_name,
      message: `Tasa de fallos alta: ${(failureRate * 100).toFixed(1)}%`,
      metric_value: failureRate,
      threshold: 0.2
    });
  }

  // Alert: Duraci√≥n promedio > 120 segundos
  if (avgDurationSeconds > 120) {
    alerts.push({
      type: 'high_latency',
      severity: 'warning',
      workflow_id: metrics.workflow_id,
      workflow_name: metrics.workflow_name,
      message: `Latencia alta: ${avgDurationSeconds.toFixed(1)}s promedio`,
      metric_value: avgDurationSeconds,
      threshold: 120
    });
  }

  // Alert: Sin ejecuciones en workflows cr√≠ticos
  const criticalWorkflows = ['lead-whatsapp-orchestrator', 'complaint-orchestrator', 'notificador-escalacion'];
  if (criticalWorkflows.some(name => metrics.workflow_name.includes(name)) && metrics.total_executions === 0) {
    alerts.push({
      type: 'no_executions',
      severity: 'critical',
      workflow_id: metrics.workflow_id,
      workflow_name: metrics.workflow_name,
      message: `Workflow cr√≠tico sin ejecuciones en la √∫ltima hora`,
      metric_value: 0,
      threshold: 1
    });
  }
});

// Preparar datos para almacenamiento
const metricsToStore = Object.values(workflowMetrics).map(metrics => ({
  ...metrics,
  fecha_reporte: now.toISOString(),
  success_rate: metrics.total_executions > 0 ? metrics.successful_executions / metrics.total_executions : 0,
  failure_rate: metrics.total_executions > 0 ? metrics.failed_executions / metrics.total_executions : 0
}));

const result = {
  metrics: metricsToStore,
  alerts: alerts,
  summary: {
    total_workflows: Object.keys(workflowMetrics).length,
    total_executions: Object.values(workflowMetrics).reduce((sum, m) => sum + m.total_executions, 0),
    total_failures: Object.values(workflowMetrics).reduce((sum, m) => sum + m.failed_executions, 0),
    critical_alerts: alerts.filter(a => a.severity === 'critical').length,
    warning_alerts: alerts.filter(a => a.severity === 'warning').length
  }
};

console.log('An√°lisis de telemetr√≠a:', result.summary);

return [{ json: result }];
