// Agrupar escalaciones por supervisor y nivel de criticidad
const escalationData = $input.all();

if (!escalationData || escalationData.length === 0) {
  return [];
}

// Estructura para agrupar por supervisor
const supervisorGroups = new Map();

escalationData.forEach(item => {
  const escalation = item.json;

  const supervisorKey = `${escalation.concesionario_id}_${escalation.supervisor_id}`;

  if (!supervisorGroups.has(supervisorKey)) {
    supervisorGroups.set(supervisorKey, {
      supervisor_id: escalation.supervisor_id,
      supervisor_nombre: escalation.supervisor_nombre,
      supervisor_email: escalation.supervisor_email,
      concesionario_id: escalation.concesionario_id,
      concesionario_nombre: escalation.concesionario_nombre,
      escalaciones_por_nivel: {
        emergencia: [],
        critico: [],
        urgente: [],
        importante: []
      },
      total_escalaciones: 0,
      max_criticidad: 'importante',
      requiere_atencion_inmediata: false
    });
  }

  const supervisorData = supervisorGroups.get(supervisorKey);

  // Estructura del item escalado
  const escalatedItem = {
    tipo: escalation.tipo_item,
    id: escalation.item_id,
    cliente: {
      telefono: escalation.telefono_cliente,
      nombre: escalation.nombre_cliente
    },
    asesor: {
      nombre: escalation.asesor_nombre,
      email: escalation.asesor_email
    },
    detalles: {
      score: escalation.score_calidad,
      nivel_interes: escalation.nivel_interes,
      horas_transcurridas: Math.round(escalation.horas_transcurridas * 10) / 10,
      motivo: escalation.motivo_escalacion,
      mensaje_resumen: escalation.tipo_item === 'lead' ? 
        escalation.mensaje_original.substring(0, 150) + '...' :
        escalation.detalle.substring(0, 150) + '...'
    }
  };

  // Categorizar por nivel de escalaci√≥n
  const nivel = escalation.nivel_escalacion;
  if (supervisorData.escalaciones_por_nivel[nivel]) {
    supervisorData.escalaciones_por_nivel[nivel].push(escalatedItem);

    // Actualizar criticidad m√°xima
    const criticidadOrden = ['importante', 'urgente', 'critico', 'emergencia'];
    const currentIndex = criticidadOrden.indexOf(supervisorData.max_criticidad);
    const newIndex = criticidadOrden.indexOf(nivel);

    if (newIndex > currentIndex) {
      supervisorData.max_criticidad = nivel;
    }

    // Marcar si requiere atenci√≥n inmediata
    if (nivel === 'emergencia' || nivel === 'critico') {
      supervisorData.requiere_atencion_inmediata = true;
    }
  }

  supervisorData.total_escalaciones++;
});

// Convertir Map a array y ordenar por criticidad
const result = Array.from(supervisorGroups.values())
  .sort((a, b) => {
    const criticidadOrden = ['importante', 'urgente', 'critico', 'emergencia'];
    return criticidadOrden.indexOf(b.max_criticidad) - criticidadOrden.indexOf(a.max_criticidad);
  });

console.log('Escalaciones agrupadas por supervisor:', {
  total_supervisores: result.length,
  emergencias: result.filter(s => s.max_criticidad === 'emergencia').length,
  criticos: result.filter(s => s.max_criticidad === 'critico').length,
  total_items_escalados: result.reduce((sum, s) => sum + s.total_escalaciones, 0)
});

return result.map(supervisor => ({ json: supervisor }));

