// Generar m√©tricas de escalaciones procesadas
const processedData = $input.all();

const summary = {
  total_supervisores_notificados: processedData.length,
  total_items_escalados: processedData.reduce((sum, item) => {
    return sum + (item.json.total_escalaciones || 0);
  }, 0),
  distribucion_criticidad: {
    emergencia: processedData.filter(item => item.json.max_criticidad === 'emergencia').length,
    critico: processedData.filter(item => item.json.max_criticidad === 'critico').length,
    urgente: processedData.filter(item => item.json.max_criticidad === 'urgente').length,
    importante: processedData.filter(item => item.json.max_criticidad === 'importante').length
  },
  atencion_inmediata_requerida: processedData.filter(item => item.json.requiere_atencion_inmediata).length,
  timestamp: new Date().toISOString(),
  execution_success: true
};

console.log('Escalaciones procesadas:', summary);

// Si hay casos de emergencia, log especial
if (summary.distribucion_criticidad.emergencia > 0) {
  console.error('‚ö†Ô∏è CASOS DE EMERGENCIA ESCALADOS:', summary.distribucion_criticidad.emergencia);
}

return [{ json: summary }];

// No hay items que requieran escalaci√≥n
console.log('No hay items que requieran escalaci√≥n en este momento');

return [{
  json: {
    message: 'No hay items pendientes de escalaci√≥n',
    timestamp: new Date().toISOString(),
    execution_success: true,
    items_found: 0
  }
}];
